INSERT INTO `processfunction` VALUES ('13', null, null, 'Văn bản đang dự thảo', null, null, 'SELECT d.Status, `d`.`Compendium`, \r\n		`d`.`Compendium2`,`d`.`DateCreated`,`d`.`DocTypeId`, `d`.`CategoryBusinessId`, `d`.`DocCode`, `d`.`InOutCode`, `dc`.`DateReceived`, `dc`.`DocumentCopyId`, \r\n	1 as `IsViewed`,`dc`.`DocumentCopyType`, `dc`.Status  as`DocCopyStatus` ,`dc`.UserCurrentId  ,`dc`.`WorkflowId`, `dc`.`NodeCurrentId`, \r\n		1 as hasUpdateViewed\r\n FROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nWHERE `dc`.`Status` = 1 AND `dc`.`UserCurrentId` = @userId AND `dc`.`DateReceived` >= @toDate AND d.CategoryBusinessId != 4\r\n ORDER BY `dc`.`DateReceived` DESC', '-- Văn bản đang dự thảo mặc định lấy ra tất cả\r\nSELECT \r\n	d.Status, `d`.`Compendium`, \r\n		`d`.`Compendium2`,`d`.`DateCreated`,`d`.`DocTypeId`, `d`.`CategoryBusinessId`, `d`.`DocCode`, `d`.`InOutCode`, `dc`.`DateReceived`, `dc`.`DocumentCopyId`, \r\n	1 as `IsViewed`,\r\n	`dc`.`DocumentCopyType`, `dc`.Status  as`DocCopyStatus`,`dc`.UserCurrentId,  `dc`.`WorkflowId`, `dc`.`NodeCurrentId`, \r\n		1 as hasUpdateViewed\r\n FROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nWHERE `dc`.`Status` = 1 \r\n	AND `dc`.`UserCurrentId` = @userId \r\n	AND d.CategoryBusinessId != 4\r\n	ORDER BY `dc`.`DateReceived` DESC', 'SELECT  `DocumentCopyId`\r\nFROM  `documentcopy` as dc\r\nWHERE  \r\n(`Status` != 1 or  `dc`.`UserCurrentId` <> @userId )\r\nAND `DocumentCopyId` IN ({0})', 'SELECT COUNT(1) FROM `documentcopy` as `dc`\r\nWHERE `dc`.`Status` = 1 AND `dc`.`UserCurrentId` = @userId ', 'SELECT 0;', 'SELECT `d`.*, `dc`.`DateReceived`, `dc`.`DocumentCopyId`, `df`.`IsViewed`,`df`.`IsDocumentImportant`,`dc`.`DocumentCopyType`, `dc`.Status  as`DocCopyStatus`,`dc`.UserCurrentId , `dc`.`WorkflowId`, `dc`.`NodeCurrentId`\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nINNER JOIN `docfinish` as `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId`\r\nWHERE `dc`.`Status` = 1 AND `dc`.`UserCurrentId` = @userId AND (@quicksearch = \'\' OR `Compendium` LIKE @quicksearch) ORDER BY `dc`.`DateReceived` DESC LIMIT @skip,@take', '', '', '1', null, null, '', '', '0', '2018-02-24', '1', '', '', '1', null, '', null, '1', '1');
INSERT INTO `processfunction` VALUES ('32', null, null, 'Văn bản mới kết thúc', null, null, 'SELECT \r\n	`d`.`Compendium`,  \r\n		`d`.`Compendium2`,\r\n	DATE_FORMAT(`d`.`DateCreated`, \'%d/%m/%Y\') as `DateCreated`,\r\n	`d`.`DocTypeId`,\r\n	`d`.`CategoryBusinessId`, \r\n	`d`.`DocCode`, \r\n	`d`.`InOutCode`, \r\n	DATE_FORMAT(`dc`.`DateReceived`, \'%d/%m/%Y\') as `DateReceived`,\r\n		`dc`.`DateReceived` as `DateReceived1`,\r\n	`dc`.`DocumentCopyId`, \r\n	`d`.`CategoryId`,\r\n	if(INSTR(dc.UserNguoiDaXem, CONCAT(\';\',@userId,\';\')) > 0, 1, 0) as `IsViewed`,\r\n	`dc`.`DocumentCopyType`, \r\n	`dc`.`Status` as`DocCopyStatus`, \r\n	`d`.UserCreatedName as UserCurrentFullName,\r\n	`dc`.WorkflowId , \r\n	`dc`.NodeCurrentId ,\r\n	1 as `DocFinishType`,\r\n	`dc`.UserCurrentId , \r\n	1 as hasUpdateViewed\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nWHERE `dc`.`Status` = 4\r\n	AND d.CategoryBusinessId != 4 \r\n	AND dc.UserNguoiThamGia LIKE CONCAT(\'%;\',@userId,\';%\')\r\n	AND `dc`.`DateReceived` >= @toDate  	\r\nORDER BY `DateReceived1` DESC;', '\r\nSELECT \r\n	`d`.`Compendium`,  \r\n		`d`.`Compendium2`,\r\n	DATE_FORMAT(`d`.`DateCreated`, \'%d/%m/%Y\') as `DateCreated`,\r\n	`d`.`DocTypeId`,\r\n	`d`.`CategoryBusinessId`, \r\n	`d`.`DocCode`, \r\n	`d`.`InOutCode`, \r\n	DATE_FORMAT(`dc`.`DateReceived`, \'%d/%m/%Y\') as `DateReceived`,\r\n		`dc`.`DateReceived` as `DateReceived1`,\r\n	`dc`.`DocumentCopyId`, \r\n	`d`.`CategoryId`,\r\n	if(INSTR(dc.UserNguoiDaXem, CONCAT(\';\',@userId,\';\')) > 0, 1, 0) as `IsViewed`,\r\n	`dc`.`DocumentCopyType`, \r\n	`dc`.`Status` as`DocCopyStatus`, \r\n	`d`.UserCreatedName as UserCurrentFullName,\r\n	`dc`.WorkflowId , \r\n	`dc`.NodeCurrentId ,\r\n	1 as `DocFinishType`,\r\n	`dc`.UserCurrentId , \r\n	1 as hasUpdateViewed\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nWHERE `dc`.`Status` = 4\r\n	AND d.CategoryBusinessId != 4 \r\n	AND dc.UserNguoiThamGia LIKE CONCAT(\'%;\',@userId,\';%\')\r\nORDER BY `DateReceived1` DESC\r\nLIMIT 200;', 'SELECT  `DocumentCopyId`\r\nFROM  `documentcopy`\r\nWHERE  `UserCurrentId` != @userId And `Status` != 4\r\nAND `DocumentCopyId` IN ({0})', 'SELECT 0;', 'SELECT COUNT(1) \r\nFROM\r\n(SELECT dc.DocumentCopyId\r\n	from `documentcopy` as `dc` \r\nWHERE `dc`.`Status` = 4 \r\n	AND dc.UserNguoiThamGia LIKE CONCAT(\'%;\',@userId,\';%\')\r\n	and if(INSTR(dc.UserNguoiDaXem, CONCAT(\';\',@userId,\';\')) > 0, 1, 0) = 0\r\nORDER BY `DateReceived` DESC \r\nLIMIT 0, 100) as d;', 'SELECT `d`.`Compendium`, `d`.`DateCreated`,`d`.`DocTypeId`,`d`.`CategoryBusinessId`, `dc`.`DateReceived`, `dc`.`DocumentCopyId`,d.DocCode, `d`.`CategoryId`,`df`.`IsViewed`,`df`.`IsDocumentImportant`,`dc`.`DocumentCopyType`, `dc`.Status  as`DocCopyStatus`,`dc`.WorkflowId , `dc`.NodeCurrentId ,`df`.`DocFinishType`,`dc`.UserCurrentId\r\n FROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nINNER JOIN `docfinish` as `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId`\r\nWHERE `dc`.`Status` = 4  AND d.CategoryBusinessId != 4    AND `df`.`UserId` = @userId AND (@quicksearch = \'\' OR `Compendium` LIKE @quicksearch) \r\nORDER BY `dc`.`DateReceived` DESC LIMIT @skip,@take', '', '', '6', null, null, '', '', '0', '2018-02-26', '0', '', '', '1', null, '', null, '28', '1');
INSERT INTO `processfunction` VALUES ('33', null, null, 'Văn bản được ủy quyền xử lý', null, null, 'SELECT \r\n	`d`.`Status`, \r\n		`d`.`Compendium`, \r\n		`d`.`Compendium2`,\r\n		DATE_FORMAT(`dc`.`DateCreated`, \'%d/%m/%Y\') as `DateCreated`,\r\n		`d`.`DocTypeId`, \r\n		`d`.`CategoryBusinessId`,  \r\n		`d`.`DocCode`, \r\n		`d`.`InOutCode`,d.Organization,\r\n		`d`.`CategoryId`, `d`.`DocumentId`, \r\n		DATE_FORMAT(`dc`.`DateReceived`, \'%d/%m/%Y\') as `DateReceived`,\r\n		`dc`.`DateReceived` as `DateReceived1`,\r\n		`d`.`IsReturned`, `d`.`IsGettingOut`, `d`.`DateAppointed`, `d`.`IsSuccess`, `d`.`UrgentId`, `d`.`IsSupplemented`, `d`.Original,\r\n		`dc`.`DocumentCopyId` as DocumentCopyId, \r\n		`dc`.`LastComment` as `LastComment`,\r\n		`dc`.`DocumentCopyType`,\r\n		`dc`.`Status` as`DocCopyStatus`,\r\n		1 as `DocFinishType`,\r\n		0 as `IsViewed`,\r\n		`u`.`FullName` as `UserCurrentFullName`, \r\n		`u`.`FirstName` as `UserCurrentFirstName`,\r\n		`dc`.UserCurrentId ,  \r\n		dc.DateOverdue , \r\n		GetDayDocumentProcess_new(dc.DateOverdue, d.Status, d.IsSuccess, d.IsReturned) as `NumberDayOverdue`, \r\n		GetDayDocumentProcess_new(d.DateAppointed, d.Status, d.IsSuccess, d.IsReturned) as  `NumberDayAppointed`, \r\n		`dc`.`WorkflowId`, \r\n		`dc`.`NodeCurrentId`, \r\n		1 as hasUpdateViewed\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\n\r\nJOIN `user` u on u.UserId = dc.UserCurrentId\r\nINNER JOIN `authorize` as `au` on `au`.AuthorizeUserId = `dc`.UserCurrentId AND `au`.`Active` = 1\r\nWHERE  `dc`.`DocumentCopyType` in (1, 2, 4, 32, 64)\r\n   and (`au`.`DocTypeId` IS NULL OR `au`.`DocTypeId` =\'\' or  `au`.DocTypeId  like CONCAT(\'%\"\', `d`.`DocTypeId`,\'\"%\'))\r\n     and `dc`.UserCurrentId = `au`.AuthorizeUserId \r\n	   and `dc`.`Status` IN (2, 16) AND d.CategoryBusinessId != 4 \r\n		 AND NOW() >= `au`.`DateBegin` \r\n		 AND NOW() <= `au`.`DateEnd` \r\n		 AND  `au`.`AuthorizeUserId` in ({0} ) and `au`.`AuthorizedU