INSERT INTO `processfunction` VALUES ('258', null, null, 'Văn bản chờ phát hành', null, null, 'SELECT  \r\n	`d`.`Status`, \r\n		`d`.`Compendium`, \r\n		`d`.`Compendium2`,\r\n		DATE_FORMAT(`dc`.`DateCreated`, \'%d/%m/%Y\') as `DateCreated`,\r\n		`d`.`DocTypeId`, \r\n		`d`.`CategoryBusinessId`,  \r\n		`d`.`DocCode`, \r\n		`d`.`InOutCode`,\r\n		`d`.`CategoryId`, `d`.`DocumentId`, \r\n		DATE_FORMAT(`dc`.`DateReceived`, \'%d/%m/%Y\') as `DateReceived`,\r\n		`dc`.`DateReceived` as `DateReceived1`,\r\n		`d`.`IsReturned`, `d`.`IsGettingOut`, `d`.`DateAppointed`, `d`.`IsSuccess`, `d`.`UrgentId`, `d`.`IsSupplemented`, \r\n		`dc`.`DocumentCopyId` as DocumentCopyId, \r\n		`dc`.`LastComment` as `LastComment`,\r\n		`dc`.`DocumentCopyType`,\r\n		`dc`.`Status` as`DocCopyStatus`,\r\n		1 as `DocFinishType`,\r\n		if(INSTR(dc.UserNguoiDaXem, CONCAT(\';\',@userId,\';\')) > 0, 1, 0) as `IsViewed`,\r\n		dc.`UserCurrentName` as `UserCurrentFullName`, \r\n		`dc`.`UserCurrentName` as `UserCurrentFirstName`,\r\n		`dc`.UserCurrentId ,  \r\n		dc.DateOverdue , \r\n		GetDayDocumentProcess_new(dc.DateOverdue, d.Status, d.IsSuccess, d.IsReturned) as `NumberDayOverdue`, \r\n		GetDayDocumentProcess_new(d.DateAppointed, d.Status, d.IsSuccess, d.IsReturned) as  `NumberDayAppointed`, \r\n		`dc`.`WorkflowId`, \r\n		`dc`.`NodeCurrentId`, \r\n		0 as hasUpdateViewed	\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nWHERE dc.`Status` in (2, 16)\r\n		AND d.CategoryBusinessId = 2 \r\n		AND d.UserCreatedId = @userId\r\n		AND ((64 & dc.NodeCurrentPermission = 64) OR (128 & dc.NodeCurrentPermission = 128))\r\n	AND `dc`.`DateReceived` >= @toDate \r\nORDER BY `DateReceived1` DESC;', 'SELECT \r\n	`d`.`Status`, \r\n		`d`.`Compendium`, \r\n		`d`.`Compendium2`,\r\n		DATE_FORMAT(`dc`.`DateCreated`, \'%d/%m/%Y\') as `DateCreated`,\r\n		`d`.`DocTypeId`, \r\n		`d`.`CategoryBusinessId`,  \r\n		`d`.`DocCode`, \r\n		`d`.`InOutCode`,\r\n		`d`.`CategoryId`, `d`.`DocumentId`, \r\n		DATE_FORMAT(`dc`.`DateReceived`, \'%d/%m/%Y\') as `DateReceived`,\r\n		`dc`.`DateReceived` as `DateReceived1`,\r\n		`d`.`IsReturned`, `d`.`IsGettingOut`, `d`.`DateAppointed`, `d`.`IsSuccess`, `d`.`UrgentId`, `d`.`IsSupplemented`, \r\n		`dc`.`DocumentCopyId` as DocumentCopyId, \r\n		`dc`.`LastComment` as `LastComment`,\r\n		`dc`.`DocumentCopyType`,\r\n		`dc`.`Status` as`DocCopyStatus`,\r\n		1 as `DocFinishType`,\r\n		if(INSTR(dc.UserNguoiDaXem, CONCAT(\';\',@userId,\';\')) > 0, 1, 0) as `IsViewed`,\r\n		`dc`.`UserCurrentName` as `UserCurrentFullName`, \r\n		`dc`.`UserCurrentName` as `UserCurrentFirstName`,\r\n		`dc`.UserCurrentId ,  \r\n		dc.DateOverdue , \r\n		GetDayDocumentProcess_new(dc.DateOverdue, d.Status, d.IsSuccess, d.IsReturned) as `NumberDayOverdue`, \r\n		GetDayDocumentProcess_new(d.DateAppointed, d.Status, d.IsSuccess, d.IsReturned) as  `NumberDayAppointed`, \r\n		`dc`.`WorkflowId`, \r\n		`dc`.`NodeCurrentId`, \r\n		0 as hasUpdateViewed	\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nWHERE dc.`Status` in (2, 16)\r\n		AND d.CategoryBusinessId = 2\r\n		AND d.UserCreatedId = @userId\r\n		AND ((64 & dc.NodeCurrentPermission = 64) OR (128 & dc.NodeCurrentPermission = 128))\r\nORDER BY `DateReceived1` DESC  \r\nLIMIT 100;', 'SELECT `dc`.`DocumentCopyId`\r\nFROM `documentcopy` as `dc` \r\nJOIN document d on d.DocumentId = dc.DocumentId\r\nWHERE (dc.`Status` not in (2, 16)\r\n		OR d.CategoryBusinessId != 2\r\n		OR d.UserCreatedId != @userId\r\n		OR (64 & dc.NodeCurrentPermission != 64) \r\n		OR (128 & dc.NodeCurrentPermission != 128))\r\n		AND `dc`.`DocumentCopyId` IN ({0});', 'SELECT Count(1)\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nWHERE dc.`Status` in (2, 16)\r\n		AND d.CategoryBusinessId = 2\r\n		AND d.UserCreatedId = @userId\r\n		AND ((64 & dc.NodeCurrentPermission = 64) OR (128 & dc.NodeCurrentPermission = 128));', 'select 0;', 'SELECT `d`.*, `dc`.`DateReceived`, `dc`.`DocumentCopyId`, `dc`.`LastComment` as `LastComment`,`dc`.`DocumentCopyType`,`dc`.Status as`DocCopyStatus`,`df`.`DocFinishType`, `df`.`IsViewed`,`df`.`IsDocumentImportant`,`u`.`FullName` as `UserCurrentFullName`, `u`.`FirstName` as `UserCurrentFirstName`,`dc`.UserCurrentId , DATEDIFF(Now(),dc.DateOverdue) AS `NumberDayOverdue` , DATEDIFF(Now(),d.DateAppointed) AS `NumberDayAppointed`, `dc`.`WorkflowId`, `dc`.`NodeCurrentId`\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nINNER JOIN `user` as `u` ON `d`.`UserCreatedId` = `u`.`UserId` \r\nINNER JOIN `docfinish` as `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId` AND `dc`.UserCurrentId = `df`.UserId \r\nWHERE `dc`.`Status` = 2 AND d.CategoryBusinessId != 4 AND `dc`.`UserCurrentId` = @userId AND `dc`.`DocumentCopyType` in (1, 2, 4, 32, 64) AND (@quicksearch = \'\' OR `Compendium` LIKE @quicksearch)  \r\nORDER BY `dc`.`DateReceived` DESC LIMIT @skip,@take', '', '', '9', null, null, '', '', '0', '2018-02-24', '3', '', '', '1', null, '', null, '1', '1');
INSERT INTO `processfunction` VALUES ('259', null, null, 'Hồ sơ đến chờ xử lý', null, null, 'SELECT `d`.*, `dc`.`DocumentCopyId`, `dc`.`DateReceived`, \r\n	1 as `IsViewed`\r\nFROM `document` as `d` \r\nJOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nWHERE `dc`.`Status` = 2 \r\nAND `d`.`CategoryBusinessId` = 4 \r\nAND `d`.DocTypeId is NULL\r\nAND `dc`.`DateReceived` >= @toDate\r\nAND `dc`.`UserCurrentId` = @userId\r\nORDER BY `dc`.`DateReceived` DESC', 'SELECT `d`.*, `dc`.`DocumentCopyId`, `dc`.`DateReceived`, \r\n	1 as `IsViewed`\r\nFROM `document` as `d` \r\nJOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nWHERE `dc`.`Status` = 2 \r\nAND `d`.`CategoryBusinessId` = 4 \r\nAND `d`.DocTypeId is NULL\r\nAND `dc`.`UserCurrentId` = @userId\r\nORDER BY `dc`.`DateReceived` DESC', 'SELECT `dc`.DocumentCopyId\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nWHERE (`dc`.`Status` != 2 \r\nOr `d`.`CategoryBusinessId` != 4 \r\nOr `d`.DocTypeId is not NULL\r\nOr `dc`.`UserCurrentId` != @userId)\r\nAND `dc`.`DocumentCopyId` IN ({0})', 'SELECT COUNT(1) \r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nWHERE `dc`.`Status` = 2 \r\nAND `d`.`CategoryBusinessId` = 4 \r\nAND `d`.DocTypeId is NULL\r\nAND `dc`.`UserCurrentId` = @userId', 'SELECT COUNT(1) FROM `document` as `d` INNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` WHERE `dc`.`Status` = 2 AND `d`.`CategoryBusinessId` = 4 AND `dc`.`UserCurrentId` = @userId', null, '', '', '0', null, null, '', '', '0', '2017-07-14', '3', '', '', '3', null, '', null, '254', '1');
INSERT INTO `processfunction` VALUES ('260', null, null, 'Văn bản theo dõi hồi báo', null, null, 'SELECT  \r\n		`d`.`DocumentId`, 		\r\n		`dc`.`DocumentCopyId`,\r\n		`d`.DocCode,\r\n		`d`.`Compendium`, \r\n		`d`.`Compendium2`,\r\n		`d`.`ProcessInfo` as AddressName,\r\n		DATE_FORMAT(`dp`.`DatePublished`, \'%d/%m/%Y\') as `DatePublished`,\r\n		`d`.`CategoryId`, \r\n		`dc`.UserCurrentId,\r\n		DATE_FORMAT(`dp`.`DateAppointed`, \'%d/%m/%Y\') as `DateAppointed`,\r\nif(INSTR(dc.UserNguoiDaXem, CONCAT(\';\',@userId,\';\')) > 0, 1, 0) as `IsViewed`,\r\n		`dp`.IsResponsed,\r\n		IF(`dp`.IsResponsed, \"Đã hồi báo\", \"Chưa hồi báo\") as `Status`,\r\n		`dp`.DateResponsed, \r\n		`dp`.DocCodeResponsed,\r\n		0 as hasUpdateViewed	\r\nFROM `doc_publish` as dp\r\nINNER JOIN `document` as `d` on dp.DocumentId = d.DocumentId\r\nINNER JOIN documentcopy as dc on dc.DocumentCopyId = dp.DocumentCopyId\r\nWHERE (`d`.`Status` = 4 OR `d`.`Status` = 16) \r\n	AND d.CategoryBusinessId != 4  \r\n	AND dp.HasLienThong = 1\r\n  AND dp.HasRequireResponse = 1\r\n	AND dp.IsResponsed = 0\r\nand d.UserCreatedId = @userId\r\n	AND `dc`.`DateReceived` >= @toDate \r\nGROUP BY dc.DocumentCopyId\r\nORDER BY `DatePublished`, `AddressName`;', 'SELECT  \r\n		`d`.`DocumentId`, 		\r\n		`dc`.`DocumentCopyId`,\r\n		`d`.DocCode,\r\n		`d`.`Compendium`, \r\n		`d`.`Compendium2`,\r\n		`d`.`ProcessInfo` as AddressName,\r\n		DATE_FORMAT(`dp`.`DatePublished`, \'%d/%m/%Y\') as `DatePublished`,\r\nif(INSTR(dc.UserNguoiDaXem, CONCAT(\';\',@userId,\';\')) > 0, 1, 0) as `IsViewed`,\r\n		`d`.`CategoryId`, \r\n		`dc`.UserCurrentId,\r\n		DATE_FORMAT(`dp`.`DateAppointed`, \'%d/%m/%Y\') as `DateAppointed`,\r\n		`dp`.IsResponsed,\r\n		IF(`dp`.IsResponsed, \"Đã hồi báo\", \"Chưa hồi báo\") as `Status`,\r\n		`dp`.DateResponsed, \r\n		`dp`.DocCodeResponsed,\r\n		0 as hasUpdateViewed	\r\nFROM `doc_publish` as dp\r\nINNER JOIN `document` as `d` on dp.DocumentId = d.DocumentId\r\nINNER JOIN documentcopy as dc on dc.DocumentCopyId = dp.DocumentCopyId\r\nWHERE (`d`.`Status` = 4 OR `d`.`Status` = 16) \r\n	AND d.CategoryBusinessId != 4  \r\n	AND dp.HasLienThong = 1\r\n  AND dp.HasRequireResponse = 1\r\n	AND dp.IsResponsed = 0\r\nand d.UserCreatedId = @userId\r\nGROUP BY dc.DocumentCopyId\r\nORDER BY `DatePublished`, `AddressName`;', 'SELECT `dc`.`DocumentCopyId`\r\nFROM `documentcopy` as `dc`\r\nWHERE (`d`.`Status` != 4 OR `d`.`Status` != 16\r\n	OR  dp.HasLienThong != 1\r\n  OR dp.HasRequireResponse != 1\r\n	OR dp.IsResponsed != 0)\r\nand d.UserCreatedId = @userId\r\n	AND d.CategoryBusinessId != 4  \r\n	AND `dc`.`DocumentCopyId` IN ({0});', 'SELECT  Count(1)\r\nfrom (select d.DocumentId\r\nFROM `doc_publish` as dp\r\nINNER JOIN `document` as `d` on dp.DocumentId = d.DocumentId\r\nINNER JOIN documentcopy as dc on dc.DocumentCopyId = dp.DocumentCopyId\r\nWHERE (`d`.`Status` = 4 OR `d`.`Status` = 16) \r\n        AND d.CategoryBusinessId != 4  \r\n        AND dp.HasLienThong = 1\r\n  AND dp.HasRequireResponse = 1\r\n        AND dp.IsResponsed = 0\r\nand d.UserCreatedId = 486\r\nGROUP BY dc.DocumentCopyId) as d;  ', 'select 0;', 'SELECT `d`.*, `dc`.`DateReceived`, `dc`.`DocumentCopyId`, `df`.`IsViewed`,`df`.`IsDocumentImportant`,`dc`.`DocumentCopyType`, `dc`.Status  as`DocCopyStatus`,  `dc`.WorkflowId , `dc`.NodeCurrentId ,`df`.`DocFinishType`, `u`.`FullName` as `UserCurrentFullName`, `u`.`FirstName` as `UserCurrentFirstName`,`dc`.UserCurrentId  FROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId`\r\nINNER JOIN `user` as `u` ON `dc`.`UserCurrentId` = `u`.`UserId` \r\nINNER JOIN `docfinish` as `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId`\r\nWHERE (`dc`.`Status` = 2 OR `dc`.`Status` = 16) AND `df`.`UserId` = @userId AND `dc`.`UserCurrentId` != @userId AND (@quicksearch = \'\' OR `Compendium` LIKE @quicksearch) ORDER BY `DateReceived` DESC LIMIT @skip ,@take', '', '', '1', null, null, '', '', '0', '2018-02-26', '3', '', '', '1', null, '', null, '257', '1');
INSERT INTO `processfunctiontype` VALUES ('1', 'Thể loại văn bản', 'SELECT * FROM `category` where `CategoryBusinessId` & 1 = 1 OR `CategoryBusinessId` & 2 = 2', 'CategoryName', '[{\"ParamName\":\"@categoryid\",\"ValueField\":\"CategoryId\",\"CompareName\":\"CategoryId\"}]');
INSERT INTO `processfunctiontype` VALUES ('2', 'Xã, phường', 'select \'1\' as Value, N\'Tiếp nhận\' as Name\r\nunion \r\nselect \'2\' as Value, N\'Thụ lý\' as Name\r\nunion \r\nselect \'3\' as Value, N\'Bổ sung\' as Name\r\nunion \r\nselect \'4\' as Value, N\'Kí duyệt\' as Name\r\nunion \r\nselect \'5\' as Value, N\'Trả kết quả\' as Name', 'Name', '@officeid');
INSERT INTO `processfunctiontype` VALUES ('3', 'Lĩnh vực hồ sơ một cửa', 'select DocFieldName, CONCAT(\';\',DocFieldId,\';\') As DocFieldId from docfield where `CategoryBusinessId` & 4 = 4', 'DocFieldName', '[{\"ParamName\":\"@docfieldid\",\"ValueField\":\"DocFieldId\",\"CompareName\":\"DocFieldIds\"}]');
INSERT INTO `processfunctiontype` VALUES ('4', 'Trạng thái hồ sơ', 'select \'1\' as Value, \'Tiếp nhận\' as Name\r\nunion \r\nselect \'2\' as Value, \'Thụ lý\' as Name\r\nunion \r\nselect \'3\' as Value, \'Bổ sung\' as Name\r\nunion \r\nselect \'4\' as Value, \'Kí duyệt\' as Name\r\nunion \r\nselect \'5\' as Value, \'Trả kết quả\' as Name', 'Name', '[{\"ParamName\":\"@status\", \"ValueField\" : \"Value\"}]');
INSERT INTO `processfunctiontype` VALUES ('5', 'Cùng hướng chuyển: văn bản đi chờ xử lý', 'SELECT distinct `dc`.`WorkflowId`, `dc`.`NodeCurrentId`, `dc`.`NodeCurrentName` \r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nWHERE `dc`.`Status` = 2  AND `d`.`CategoryBusinessId` = 2 AND `dc`.`UserCurrentId` = @userId AND `dc`.`DocumentCopyType` in (1, 2, 4, 32, 64)', 'NodeCurrentName', '[{\"ParamName\":\"@workflowId\",\"ValueField\":\"WorkflowId\",\"CompareName\":\"WorkflowId\"},{\"ParamName\":\"@nodeCurrentId\",\"ValueField\":\"NodeCurrentId\",\"CompareName\":\"NodeCurrentId\"}]');
INSERT INTO `processfunctiontype` VALUES ('6', 'Cùng hướng chuyển', 'SELECT `dc`.`WorkflowId`, `dc`.`NodeCurrentId`, CONCAT(\'Chuyển - \' ,`dc`.`NodeCurrentName` ) as \'DisplayName\' \r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nWHERE `dc`.`UserCurrentId` = @userId and  dc.`Status`=2 \r\n And `dc`.`WorkflowId` is not NULL  And `dc`.`NodeCurrentId` is not NULL\r\n\r\n AND `dc`.`DocumentCopyType` in (1, 2, 4, 32, 64)\r\nGROUP BY `dc`.`WorkflowId`, `dc`.`NodeCurrentId`', 'DisplayName', '[{\"ParamName\":\"@workflowId\",\"ValueField\":\"WorkflowId\",\"CompareName\":\"WorkflowId\"},{\"ParamName\":\"@nodeCurrentId\",\"ValueField\":\"NodeCurrentId\",\"CompareName\":\"NodeCurrentId\"}]');
INSERT INTO `processfunctiontype` VALUES ('7', 'Cùng người gửi', 'SELECT DISTINCT  `dc`.`LastUserIdComment`, `dc`.LastUserComment\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nWHERE  dc.`Status`= 2 and   `dc`.`UserCurrentId` = @userId  and `dc`.`LastUserIdComment` is not null\r\n AND `d`.`CategoryBusinessId` = 2  AND `dc`.`DocumentCopyType` in (1, 2, 4, 32, 64)\r\n', 'LastUserComment', '[{\"ParamName\":\"@userSendId\",\"ValueField\":\"LastUserIdComment\",\"CompareName\":\"LastUserIdComment\"}]');
INSERT INTO `processfunctiontype` VALUES ('8', 'Theo loại văn bản', 'SELECT dt.DocTypeId ,dt.DocTypeName as `DisplayName`\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nINNER JOIN doctype as dt on dt.DocTypeId=dc.DocTypeId\r\nWHERE  dc.`Status`= 2 and `dc`.`UserCurrentId` = @userId\r\nAND `d`.`CategoryBusinessId` = 2 AND `dc`.`DocumentCopyType` in (1, 2, 4, 32, 64)\r\nGROUP BY dt.DocTypeId ', 'DisplayName', '[{\"ParamName\":\"@docTypeId\",\"ValueField\":\"DocTypeId\",\"CompareName\":\"DocTypeId\"}]');
INSERT INTO `processfunctiontype` VALUES ('9', 'Theo đơn vị gửi', 'SELECT d.InOutPlace, dept.DepartmentName  as `DisplayName`\r\nFROM `document` as `d` \r\nINNER JOIN `documentcopy` as `dc` ON `d`.`DocumentId` = `dc`.`DocumentId` \r\nINNER JOIN department dept on dept.DepartmentPath = d.InOutPlace\r\nWHERE  dc.`Status`= 2 and `dc`.`UserCurrentId` = @userId\r\nAND d.InOutPlace is not null\r\nAND `d`.`CategoryBusinessId` = 2 AND `dc`.`DocumentCopyType` in (1, 2, 4, 32, 64)\r\nGROUP BY d.InOutPlace ', 'DisplayName', '[{\"ParamName\":\"@inOutPlace\",\"ValueField\":\"InOutPlace\",\"CompareName\":\"InOutPlace\"}]');
