INSERT INTO `report` VALUES ('211', null, 'Văn bản đến', '210', null, 'Select 	\r\n	d.InOutCode, \r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.Organization, \r\n	d.CategoryName,\r\n	d.CategoryId,\r\n	dc.UserCurrentId,\r\n	dc.CurrentDepartmentName as DepartmentName,\r\n	dc.UserCurrentName as CurrentUser, \r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%y\') as ExpireDate,\r\n	d.DateAppointed,\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	GetUrgent(d.UrgentId) as UrgentName\r\n  #groupNameQuery\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 1	\r\n	AND d.CategoryId is NOT NULL\r\n	AND d.UserCreatedId = @userId\r\n	AND d.Original = 0\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n        AND (@userId=0 or dc.UserNguoiThamGia LIKE CONCAT(\'%;\',@userId,\';%\'))\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'Select 	\r\n	Count(1)	\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = dc.UserCurrentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nLEFT JOIN `user` u2 on u2.UserId = d.UserSuccessId\r\nLEFT JOIN (\r\n	SELECT d2.Compendium, d2.DocCode, d2.DateArrived, drl.RelationId\r\n	FROM document d2\r\n	JOIN docrelation drl on drl.DocumentId = d2.DocumentId\r\n	WHERE drl.RelationType = 2\r\n) rd on rd.RelationId = d.DocumentId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 1\r\n	AND dc.UserNguoiThamGia LIKE CONCAT(\'%;\',@userId,\';%\')\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue);', 'SoHoSo_VanBanDen_XLVB.rpt', 'aa72cbca2bc74b6aa8bc98d725e20f60.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '258', 'SoHoSo_VanBanDen_XLVB_Group.rpt', '92ff41849425483e957bee064552b1a3.rpt', '');
INSERT INTO `report` VALUES ('212', null, 'Văn bản đi', '210', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.ProcessInfo, \r\n  DATE_FORMAT(d.DateSuccess, \'%d/%m/%y\') as DateSuccess,\r\n	d.CategoryName,\r\n	d.CategoryId,\r\n	dc.UserCurrentId,\r\n	dc.CurrentFullName as CurrentUser, \r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%y\') as ExpireDate,\r\n	d.DateAppointed,\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	GetUrgent(d.UrgentId) as UrgentName\r\n  #groupNameQuery\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 2	\r\n	AND d.CategoryId is NOT NULL\r\n	AND d.UserCreatedId = @userId\r\n	AND d.Original = 0\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n        AND (@userId=0 or dc.UserNguoiThamGia LIKE CONCAT(\'%;\',@userId,\';%\'))\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'Select \r\n	Count(1)\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN `user` as ucreate on ucreate.UserId = d.UserCreatedId\r\nJOIN `user` as ucurrent on ucurrent.UserId = dc.UserCurrentId\r\nLEFT JOIN category on category.CategoryId = d.CategoryId\r\nLEFT JOIN `user` usuccess on usuccess.UserId = d.UserSuccessId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 2\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND d.UserCreatedId = @userId\r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0	\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n  AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)', 'BaocaoCanhan_di_xlvb.rpt', '9c84a3ab52494f098807d1895656bdec.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '263', 'BaocaoCanhan_di_xlvb_Group.rpt', 'f163e730bbe3469baa3ad51317ad5999.rpt', '');
INSERT INTO `report` VALUES ('213', null, 'Văn bản quá hạn', '210', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.InOutCode,\r\n	d.CategoryName,\r\n	d.CategoryId,\r\n	d.UserCreatedId,\r\n	dc.UserCurrentId,\r\n	d.UserCreatedName as UserCreated, \r\ndc.UserCurrentName as UserCurrent,\r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%y\') as ExpireDate,\r\n	DATE_FORMAT(d.DateFinished, \'%d/%m/%y\') as FinishDate,\r\n	d.DateFinished,\r\n	d.DateAppointed,\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	GetUrgent(d.UrgentId) as UrgentName,\r\n	dc.DocumentCopyId,\r\n	d.CategoryBusinessId,\r\n	GetCategoryBusiness(d.CategoryBusinessId) as CategoryBusiness\r\n  #groupNameQuery\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nWHERE dc.DocumentCopyType in (1, 2)\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId != 4	\r\n	AND d.CategoryId is NOT NULL\r\n	AND dc.UserCurrentId = @userId\r\n	AND d.DateAppointed is not NULL \r\n	AND ((d.DateFinished is not null and Date(d.DateFinished) > Date(d.DateAppointed)) or (d.DateFinished is null and  Date(NOW()) > Date(d.DateAppointed)))\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n        AND (@userId=0 or dc.UserNguoiThamGia LIKE CONCAT(\'%;\',@userId,\';%\'))\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'select count(1)\r\nFROM `document` AS `d`\r\nJOIN `documentcopy` AS `dc` ON `dc`.`DocumentId` = `d`.`DocumentId`\r\nJOIN `user` AS `u` ON `u`.`UserId` = `dc`.`UserCurrentId`\r\nJOIN `user` AS `u1` ON `u1`.`UserId` = `d`.`UserCreatedId`\r\nJOIN `doctype` AS `dt` ON `d`.`DocTypeId` = `dt`.`DocTypeId`\r\nJOIN `docfinish` AS `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId` \r\nJOIN `category` ON `d`.`CategoryId` = `category`.`CategoryId`\r\nLEFT OUTER JOIN `user` AS `u2` ON `u2`.`UserId` = `d`.`UserSuccessId`\r\nLEFT OUTER JOIN `docfield` AS `df2` ON `dt`.DocFieldId = `df2`.`DocFieldId`\r\nLEFT OUTER JOIN `doc_publish` AS `dp` ON `dp`.`DocumentCopyId` = `dc`.`DocumentCopyId`\r\n     WHERE `df`.`UserId` = @UserId and\r\n`d`.`Status` IN(4) and\r\n	  d.DateAppointed < d.DateSuccess\r\nAND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n    AND (@treeGroupValue is null or @treeGroupValue = \'\' or #treeGroup = @treeGroupValue)\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)', 'BaocaoCanhan_quahan.rpt', 'b6563fbf15a24a0a9302ce3dc7dba7b3.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '264', 'BaocaoCanhan_quahan_Group.rpt', 'e0308ed905ad44b6ad11fb10a9dc8e61.rpt', '');
INSERT INTO `report` VALUES ('214', null, 'HSMC _ Danh sách tiếp nhận', '210', null, 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateReturned, \'%d/%m/%Y\') as DateReturned\r\n        #groupNameQuery\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.UserCreatedId = @userId\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND d.UserCreatedId = @userId\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)', 'BaoCao_TiepNhanHoSo.rpt', 'd7657df9127840babff4c26e1eae64b3.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '470', null, null, '');
