INSERT INTO `report` VALUES ('220', null, 'Sổ văn bản đến', '219', '[11]', 'Select 	\r\n	d.InOutCode, \r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.Organization, \r\n	d.CategoryName,\r\n	d.CategoryId,\r\n	dc.UserCurrentId,\r\n	dc.CurrentDepartmentName as DepartmentName,\r\n	dc.UserCurrentName as CurrentUser, \r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%y\') as ExpireDate,\r\n	DATE_FORMAT(d.DateArrived, \'%d/%m/%y\') as DateArrived,\r\n	DATE_FORMAT(d.DatePublished, \'%d/%m/%y\') as DatePublished,\r\n	d.DateAppointed,\r\n	d.`Status` as Status ,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	GetUrgent(d.UrgentId) as UrgentName,\r\n	dc.DocumentCopyId,\r\n	sd.StoreId\r\n  #groupNameQuery\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN store_doc sd on sd.DocumentId = d.DocumentId\r\nJOIN store st on st.StoreId = sd.StoreId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 1\r\n	AND d.CategoryId is NOT NULL\r\n	AND (st.UserId = @userId\r\n					OR st.UserViewIds like CONCAT(\'%,\',@userId,\',%\')\r\n					OR st.UserViewIds like CONCAT(\'[\',@userId,\',%\')\r\n					OR st.UserViewIds like CONCAT(\'%,\',@userId,\']\'))\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'Select Count(1)\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN store_doc sd on sd.DocumentId = d.DocumentId\r\nJOIN store st on st.StoreId = sd.StoreId\r\nJOIN `user` u on u.UserId = dc.UserCurrentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nLEFT JOIN `user` u2 on u2.UserId = d.UserSuccessId\r\nLEFT JOIN (\r\n	SELECT d2.Compendium, d2.DocCode, d2.DateArrived, drl.RelationId\r\n	FROM document d2\r\n	JOIN docrelation drl on drl.DocumentId = d2.DocumentId\r\n	WHERE drl.RelationType = 2\r\n) rd on rd.RelationId = d.DocumentId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 1\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n        AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)', 'SoHoSo_VanBanDen_XLVB.rpt', 'affbbb26711e401db3a11aea6de6c3db.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'select 0;', 'select 0;', '', '258', 'SoHoSo_VanBanDen_XLVB_Group.rpt', 'fd10f4f8c56c467bbb286d7c24251dc9.rpt', '');
INSERT INTO `report` VALUES ('221', null, 'Sổ văn bản đi', '219', '[19]', 'Select \r\n	d.DocCode,\r\n	d.Compendium,\r\n	d.InOutPlace as InOutPlace,\r\n	d.UserCreatedName as UserCreated,\r\n	dc.CurrentDepartmentName as DepartmentName,\r\n	dc.UserCurrentName as UserCurrent,\r\n	d.UserSuccessName as UserSuccess,\r\n	DATE_FORMAT(d.DateSuccess, \'%d/%m/%y\') as DateSuccess,\r\n	DATE_FORMAT(d.DateFinished, \'%d/%m/%y\') as DateFinished,\r\n	DATE_FORMAT(d.DateArrived, \'%d/%m/%y\') as DateArrived,\r\n	SUBSTRING_INDEX(if(POSITION(\"Nơi nhận:\" IN d.ProcessInfo) = 0, SUBSTRING_INDEX(d.ProcessInfo, \"Nơi nhận trong cơ quan:\", 1), SUBSTRING_INDEX(d.ProcessInfo, \"Nơi nhận:\", -1)), \"Thông báo:\", 1) as Address,\r\n	d.CategoryName as CategoryName,\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	GetUrgent(d.UrgentId) as UrgentName,\r\n	sd.StoreId,\r\n	d.UserSuccessId,\r\n	d.UserCreatedId,\r\n	dc.DocumentCopyId,\r\n	d.CategoryId\r\n  #groupNameQuery\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN store_doc sd on sd.DocumentId = d.DocumentId\r\nJOIN store st on st.StoreId = sd.StoreId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 2\r\n	AND (st.UserId = @userId\r\n			OR st.UserViewIds like CONCAT(\'%,\',@userId,\',%\')\r\n			OR st.UserViewIds like CONCAT(\'[\',@userId,\',%\')\r\n			OR st.UserViewIds like CONCAT(\'%,\',@userId,\']\'))\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0	\r\n  AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'Select \r\n	count(1)\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN `user` as ucreate on ucreate.UserId = d.UserCreatedId\r\nJOIN `user` as ucurrent on ucurrent.UserId = dc.UserCurrentId\r\nJOIN store_doc sd on sd.DocumentId = d.DocumentId\r\nJOIN store st on st.StoreId = sd.StoreId\r\nLEFT JOIN category on category.CategoryId = d.CategoryId\r\nLEFT JOIN `user` usuccess on usuccess.UserId = d.UserSuccessId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 2\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0	\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n  AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue);\r\n', 'SoHoSo_VanBanDi_fix.rpt', 'a7ccaf2e448f4438ad07f27360408f7f.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '260', 'SoHoSo_VanBanDi_XLVB_Group.rpt', '4f6818da7e1b403288de64cc3f0cbf92.rpt', '');
