INSERT INTO `report` VALUES ('223', null, 'Sổ theo dõi hồ sơ', '222', null, 'SELECT \r\n        `dt`.DoctypeName as DoctypeName,\r\n        `dt`.DoctypeId as DoctypeId,\r\n        d.DocCode, 1 as Amount, d.CitizenName, \r\n	CONCAT(d.Phone,\' - \',d.Address) as Address, \"\" as `Department`, \r\n	DATE_FORMAT(d.DateCreated,\'%d/%m/%y\') as DateCreated, \r\n	DATE_FORMAT(d.DateAppointed,\'%d/%m/%y\') as DateAppointed, \r\n	DATE_FORMAT(d.DateCreated,\'%d/%m/%y\') as DateResult, \r\n	DATE_FORMAT(d.DateSuccess,\'%d/%m/%y\') as DateFinished,\r\n	DATE_FORMAT(d.DateReturned,\'%d/%m/%y\') as DateReturned\r\n        #groupNameQuery\r\nFROM document d\r\nJOIN documentcopy dc ON dc.DocumentId = d.DocumentId\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJoin docfield df on df.DocFieldId = dt.DocFieldId\r\nWHERE d.CategoryBusinessId = 4\r\n        AND d.`Status` NOT IN (1, 8)\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n        And dc.DocumentCopyType  in (1, 64)\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY dt.DocTypeName, d.DocCode\r\nLIMIT @skip, @take;', 'SELECT Count(1)\r\nFROM document d\r\nJOIN documentcopy dc ON dc.DocumentId = d.DocumentId\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJoin docfield df on df.DocFieldId = dt.DocFieldId\r\nWHERE d.CategoryBusinessId = 4\r\n        AND d.`Status` NOT IN (1, 8)\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n        And dc.DocumentCopyType in (1, 64)\r\n       AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY dt.DocTypeName;', 'BaoCao_SoTheoDoiHoSo.rpt', '931588242bd64853a9830895039ef0c3.rpt', null, null, null, '', '', '0001-01-01 00:00:00