INSERT INTO `report` VALUES ('224', null, 'Báo cáo tiếp nhận hồ sơ', '222', null, 'Select \r\n        `dt`.DoctypeName as DoctypeName,\r\n        `dt`.DoctypeId as DoctypeId,\r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateReturned, \'%d/%m/%Y\') as DateReturned\r\n        #groupNameQuery\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY d.DocCode\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)', 'BaoCao_TiepNhanHoSo.rpt', 'd7657df9127840babff4c26e1eae64b3.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '468', 'BaoCao_TiepNhanHoSo.rpt', 'e1b939a486b845198b23d59051f2af15.rpt', '');
INSERT INTO `report` VALUES ('225', null, 'Hồ sơ đúng hẹn', '224', null, 'Select \r\n        `dt`.DoctypeName as DoctypeName,\r\n        `dt`.DoctypeId as DoctypeId,\r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateSuccess, \'%d/%m/%Y\') as DateSuccess,\r\n	DATE_FORMAT(IF(`d`.DateReturned is NULL, d.DateFinished, `d`.DateReturned ) , \'%d/%m/%Y\') as DateReturned,\r\n	GetStatus(d.`Status`) as Note\r\n        #groupNameQuery\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 1\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 1', 'BaoCao_TiepNhanHoSo - DungHan.rpt', 'e24a959e95b34e819514aa7a167253c7.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'select 0;', 'select 0;', '', '470', 'BaoCao_TiepNhanHoSo - DungHan.rpt', '1cd08b0a33ae47a2a76b3a81aeba71d3.rpt', '');
