Calibri;}\n.MsoChpDefault\n	{mso-style-type:export-only;\n	mso-default-props:yes;\n	font-size:10.0pt;\n	mso-ansi-font-size:10.0pt;\n	mso-bidi-font-size:10.0pt;\n	mso-fareast-font-family:Calibri;}\n@page Section1\n	{size:612.0pt 792.0pt;\n	margin:72.0pt 72.0pt 72.0pt 72.0pt;\n	mso-header-margin:36.0pt;\n	mso-footer-margin:36.0pt;\n	mso-paper-source:0;}\ndiv.Section1\n	{page:Section1;}\n-->	</style>\n<p></p>\n<p>\n	&nbsp;</p>\n<p>\n	&nbsp;</p>\n<p>\n	&nbsp;</p>\n<p>\n	&nbsp;</p>\n<p>\n	</p><div id=\"RadEditorStyleKeeper16\" style=\"display:none;\">&nbsp;</div><div id=\"RadEditorStyleKeeper16\" style=\"display:none;\">&nbsp;</div><link reoriginalpositionmarker=\"RadEditorStyleKeeper16\" href=\"file:///C:\\DOCUME~1\\thangpv\\LOCALS~1\\Temp\\msohtmlclip1\\01\\clip_themedata.thmx\" rel=\"themeData\">\n<p></p>\n', null, null, null, '1', '16', '', 'PhieuIn_BienNhanBoSung.rpt', '1b5d9f28a53946e180dbd2a0178c0bec.rpt', 'SELECT doc.*, feepaper.PaperFeeName, feepaper.Amount, feepaper.PaperFeeType from\r\n(SELECT\r\n		d.DocumentId, d.DocCode, d.CitizenName, d.Address, \r\n		DATE_FORMAT(supp.DateReceived, \'%H giờ %i phút, ngày %d tháng %m năm %Y\') as DateReceived, \r\n		DATE_FORMAT(supp.NewDateAppointed, \'%H giờ %i phút, ngày %d tháng %m năm %Y\') as DateAppointed,\r\n		dt.DocTypeName as DoctypeName\r\n	FROM documentcopy dc \r\n	INNER JOIN document d ON dc.DocumentId = d.DocumentId \r\n	INNER JOIN doctype dt on dt.DocTypeId = dc.DocTypeId\r\n	INNER JOIN (\r\n              SELECT    MAX(SupplementaryId) SupplementaryId, DocumentId, DateReceived, NewDateAppointed \r\n              FROM      supplementary \r\n							WHERE @suppId = 0 OR SupplementaryId = @suppId\r\n              GROUP BY  SupplementaryId\r\n          ) supp ON supp.DocumentId = d.DocumentId\r\n	Where dc.DocumentCopyId = @docCopyId) as doc\r\n\r\nLEFT JOIN\r\n	(\r\n		SELECT dp.DocumentId, dp.PaperName as PaperFeeName, CONCAT(dp.Amount, \" bản\") as Amount, \"Giấy tờ đã nộp bổ sung\" as PaperFeeType\r\n		from doc_paper dp where dp.Type = 2 AND (@suppId = 0 OR dp.SupplementaryId = @suppId)\r\n\r\n		UNION ALL\r\n\r\n		SELECT df.DocumentId, df.FeeName as PaperFeeName, CONCAT(FORMAT(df.Price, 0, \"vi_VN\"), \" Đồng\") as Amount, \"Lệ phí nộp bổ sung\" as PaperFeeType\r\n		from doc_fee df where df.Type = 2 AND (@suppId = 0 OR df.SupplementaryId = @suppId))\r\n	as feepaper on doc.DocumentId = feepaper.DocumentId', '0', null);
INSERT INTO `template` VALUES ('16', 'In biên nhận trả kết quả', '', null, null, null, '1', '8', '', 'PhieuIn_BienNhanTraKetQua.rpt', '8b6938a9275b4d18ae4346247f1d59b1.rpt', 'SELECT \r\n	d.DocCode, d.CitizenName, d.Address as CitizenAddress, d.Email as CitizenEmail, d.Phone as CitizenPhone, d.Compendium, \r\n	d.ReturnNote as ReturnedNode, DATE_FORMAT(d.DateReturned, \'%H giờ %i phút, ngày %d tháng %m năm %Y\') as DateReturned, ur.FullName as UserReturnedName,\r\n	DATE_FORMAT(d.DateCreated, \'%H giờ %i phút, ngày %d tháng %m năm %Y\') as DateCreated, \r\n	us.FullName as UserSuccessName, DATE_FORMAT(d.DateSuccess, \'%H giờ %i phút, ngày %d tháng %m năm %Y\') as DateSuccess,\r\n	dp.PaperName as Paper, dp.Amount\r\nFROM document d\r\nInner JOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nLEFT JOIN `user` ur on ur.UserId = d.UserReturnedId\r\nLEFT JOIN `user` us on us.UserId = d.UserSuccessId\r\nLEFT JOIN doc_paper dp on dp.DocumentId = d.DocumentId AND dp.Type = 3\r\nWHERE dc.DocumentCopyId = @docCopyId', '0', '');
