INSERT INTO `template` VALUES ('30', 'eMail_Thông báo tiếp nhận hồ sơ', '<table border=\"0\" cellpadding=\"0\" width=\"760\" style=\"width: 569.65pt; margin-left: -40.9pt; border-top-style: none; border-left-style: none; border-bottom-width: 0px; border-right-width: 0px;\">\n    <tbody>\n        <tr style=\"height: 40.9pt;\">\n            <td style=\"width: 299.25pt; border-top-width: 0px; border-left-width: 0px; border-bottom-style: none; border-right-style: none; padding: 0.75pt; height: 40.9pt;\">\n            <p align=\"center\"><b>VĂN PHÒNG UBND TỈNH</b></p>\n            <p align=\"center\"><b><u>BỘ PHẬN TN &amp; TKQ</u></b><b></b></p>\n            </td>\n            <td style=\"width: 265.9pt; border-top-width: 0px; border-left-width: 0px; border-bottom-style: none; border-right-style: none; padding: 0.75pt; height: 40.9pt;\">\n            <p align=\"center\"><b>CỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM</b></p>\n            <p align=\"center\"><b><u>Độc lập - Tự do - Hạnh phúc</u></b><u></u></p>\n            </td>\n        </tr>\n    </tbody>\n</table>\n<p align=\"center\"><font style=\"font-size: 9pt;\"><br>\n</font><b><br>\n</b><b><font size=\"5\">THÔNG BÁO</font></b> </p>\n<p align=\"center\"><b><font color=\"#0070c0\">Về việc đồng ý tiếp nhận hồ sơ đăng ký qua mạng</font></b><font style=\"font-size: 9pt;\"><br>\n<br>\n</font></p>\n<p>Kính gửi: Ông (bà) <b>{cd_ten}</b></p>\n<p><b>Bộ phận Tiếp nhận và Trả kết quả Tập trung cấp Tỉnh&nbsp;</b>xin thông báo:</p>\n<p>Hồ sơ của Ông (Bà) đã được chính thức tiếp nhận và thụ lý tại Bộ phận Tiếp nhận và Trả kết quả Tập trung cấp Tỉnh.<br>\nÔng (bà) vui lòng đọc kỹ các thông tin dưới đây.<br>\n<br>\n<b>Thông tin hồ sơ đã đăng ký:<br>\n</b><font style=\"text-indent: -0.25in;\">-</font><font style=\"text-indent: -0.25in; font-stretch: normal; font-size: 7pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </font><font style=\"text-indent: -0.25in; font-size: 10.5pt;\">Loại hồ sơ đăng ký</font><font style=\"text-indent: -0.25in; font-size: 10.5pt;\">:&nbsp;{doc_loai_ho_so}</font><font style=\"text-indent: -0.25in;\"><br>\n</font><font style=\"text-indent: -0.25in;\">-</font><font style=\"text-indent: -0.25in; font-stretch: normal; font-size: 7pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </font><font style=\"text-indent: -0.25in;\">Họ tên người đăng ký</font><font style=\"text-indent: -0.25in;\">:&nbsp;{cd_ten}<br>\n- &nbsp; &nbsp; Số CMT: {cd_cmnd} &nbsp; &nbsp; &nbsp; &nbsp; Số điện thoại: &nbsp;{cd_phone}&nbsp; &nbsp; &nbsp; Thư điện tử&nbsp;{cd_email}&nbsp;</font></p>\n<b>Thông tin tiếp nhận hồ sơ:</b><br>\n- &nbsp; &nbsp; Cơ quan thụ lý hồ sơ:&nbsp;{office_name}<br>\n<font style=\"text-indent: -0.25in;\">-</font><font style=\"text-indent: -0.25in; font-stretch: normal; font-size: 7pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font style=\"text-indent: -0.25in;\">Mã hồ sơ</font><font style=\"text-indent: -0.25in;\">:&nbsp;{doc_code}</font><br>\n<font style=\"text-indent: -0.25in;\">-</font><font style=\"text-indent: -0.25in; font-stretch: normal; font-size: 7pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </font><font style=\"text-indent: -0.25in;\">Ngày tiếp nhận</font><font style=\"text-indent: -0.25in;\">:</font><b style=\"text-indent: -0.25in;\">&nbsp;</b><font style=\"text-indent: -0.25in;\">{doc_datecreated}</font><br>\n<font style=\"text-indent: -0.25in;\">-</font><font style=\"text-indent: -0.25in; font-stretch: normal; font-size: 7pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </font><font style=\"text-indent: -0.25in;\">Ngày hẹn trả hồ sơ</font><font style=\"text-indent: -0.25in;\">:&nbsp;{doc_dateappointed}<br>\n<br>\n<b>Email này thay cho Giấy biên nhận và Hẹn trả kết quả, Ông (bà) vui lòng In nội dung eMail này ra và liên hệ với Bộ phận Tiếp nhận và Trả kết quả Tập trung cấp Tỉnh để nộp hồ sơ giấy hoặc nhận kết quả.</b></font>\n<p>\n<font style=\"line-height: 107%;\" size=\"3\">Để tra cứu tiến độ xử lý cũng như cập nhật thông tin hồ sơ, Ông (bà) vui\nlòng nhấp chuột</font><font style=\"font-size: 13.5pt; line-height: 107%;\">&nbsp;</font> <a style=\"border: 1px solid #cccaca; padding: 4px 8px;\" href=\"{online_link_access}\" target=\"_blank\"><b>vào đây</b></a></p>\n<p>Cám ơn Ông (Bà) đã sử dụng dịch vụ của chúng tôi!</p>\n<div align=\"center\"><font face=\"\'times new roman\'\"><br><div align=\"left\"><b>Bộ phận Tiếp nhận và Trả kết quả Tập trung cấp Tỉnh</b><br>Địa chỉ: {office_address}<br>Số điện thoại:&nbsp;{office_phone} &nbsp; &nbsp;Thư điện tử&nbsp;{office_mail}</div><div align=\"left\"><font style=\"font-size: 12px;\"><br></font></div>\n</font></div>', null, null, null, '2', '1', '', null, null, null, '0', 'Trung tâm MCTT cấp Tỉnh: Thông báo tiếp nhận hồ sơ đăng ký qua mạng');
INSERT INTO `template` VALUES ('31', 'eMail_Thông báo từ chối hồ sơ đăng ký qua mạng', '<p>\n</p>\n<table border=\"0\" cellpadding=\"0\" width=\"706\" style=\"width: 529.45pt; margin-left: -39.5pt; border-top-style: none; border-left-style: none; border-bottom-width: 0; border-right-width: 0;\">\n    <tbody>\n        <tr style=\"height: 37.35pt;\">\n            <td valign=\"top\" style=\"border-top-width: 0; border-left-width: 0; border-bottom-style: none; border-right-style: none; padding: 0.75pt; height: 37.35pt;\">\n            <p align=\"center\"><b>VĂN PHÒNG UBND TỈNH</b></p>\n            <p align=\"center\"><b><u>BỘ PHẬN TN &amp; TKQ</u></b><b></b></p>\n            </td>\n            <td valign=\"top\" style=\"border-top-width: 0; border-left-width: 0; border-bottom-style: none; border-right-style: none; padding: 0.75pt; height: 37.35pt;\">\n            <p align=\"center\"><b>CỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM</b></p>\n            <p align=\"center\"><b><u>Độc lập - Tự do - Hạnh phúc</u></b><u></u></p>\n            </td>\n        </tr>\n    </tbody>\n</table>\n<p align=\"center\"><br></p><p align=\"center\"><b>\nTHÔNG BÁO</b></p>\n<p align=\"center\"><b><font color=\"red\">Về việc từ chối tiếp nhận hồ sơ đăng ký qua mạng</font></b></p>\n<p align=\"center\"><b><font color=\"red\">&nbsp;</font></b></p>\n<p>Kính gửi : Ông (Bà)&nbsp;<b>{online_citizen_name} </b></p>\n<p>Ngày {online_date_command}, Trung tâm Tiếp nhận và Trả kết quả Tập trung cấp Tỉnh đã &nbsp;nhận được hồ sơ đăng ký của Ông (Bà) qua hệ thống dịch vụ công trực tuyến.</p>\n<p>Chúng tôi đã xem xét tính hợp lệ hồ sơ của Ông (Bà) và chúng tôi rất lấy làm tiếc vì hồ sơ của Ông (bà) sẽ không được tiếp nhận và thụ lý tại cơ quan một cửa. Thông tin chi tiết, chúng tôi xin được nêu ra phía dưới đây:</p>\n<p>-&nbsp;&nbsp; Loại hồ sơ đã đăng ký :&nbsp;<b>{online_doctype_name}</b></p>\n<p>-&nbsp;&nbsp; Họ tên người đăng ký :&nbsp;<b>{online_citizen_name}</b></p>\n<p>-&nbsp;&nbsp; Mã hồ sơ đăng ký:&nbsp;<b>{online_doccode}</b></p>\n<p>-&nbsp;&nbsp; Ngày đăng ký:&nbsp;<b>{online_date_register}</b></p>\n<p>-&nbsp;&nbsp; Lý do từ chối tiếp nhận :&nbsp;<font color=\"red\">{online_comment}</font></p>\n<p><font color=\"#ff0000\">&nbsp;<br>\n</font>Ông bà có thể tra cứu thêm thông tin hồ sơ đã đăng ký<b> <a href=\"{online_link_access}\">tại đây</a></b>.</p>\n<p><font color=\"#ff0000\">&nbsp;<br>\n</font>Mọi thắc mắc (nếu có) của Ông (bà) xin vui lòng liên hệ trực tiếp với Trung Tâm Một cửa Tập trung cấp Tỉnh tại địa chỉ:</p>\n<p>198 Bạch Đằng, Phước Trung, Bà Rịa, Bà Rịa - Vũng Tàu. Số điện thoại: (064)3 512.228 - Fax: (064)3 512.224 - Email: sotttt@baria-vungtau.gov.vn</p>\n<p>Xin cảm ơn Ông (bà) đã sử dụng dịch vụ của chúng tôi.</p>\n<div align=\"center\"><font style=\"font-size: 12px;\" face=\"arial\"><br>\n</font></div>', null, null, null, '2', '128', '', null, null, null, '0', 'Trung tâm MCTT cấp Tỉnh: Thông báo từ chối hồ sơ đăng ký qua mạng');
INSERT INTO `template` VALUES ('32', 'In biên nhận và hẹn trả kết quả', '<table width=\"1100\" cellspacing=\"0\" cellpadding=\"0\" style=\"margin: 5px; border: 1px dotted black;\">      <tbody>          <tr>              <td valign=\"top\" align=\"center\" style=\"width: 180px;\"><font class=\"style12\"><font style=\"font-size: 12px;\" face=\"\'times new roman\'\"><b class=\"style14\"><font class=\"style12\" style=\"font-size: 12px;\"><b class=\"style14\"><font style=\"font-size: 14px;\">TỈNH QUẢNG NGÃI<br>              </font></b></font></b></font><font face=\"\'times new roman\'\"><font style=\"font-size: 14px;\"><b><font style=\"font-size: 13px;\">SỞ THÔNG TIN TRUYỀN THÔNG</font><br>              </b></font><font face=\"\'times new roman\'\" style=\"font-family: \'times new roman\';\">              ------------</font><br>              <font face=\"\'times new roman\'\" style=\"font-family: \'times new roman\';\">              Số:&nbsp;{doc_code}</font><br>              <font face=\"\'times new roman\'\" style=\"font-family: \'times new roman\';\">              Số ĐT: </font><b style=\"font-family: \'times new roman\';\">064.3852652</b></font><font class=\"style12\"> </font><br>              </font></td>              <td valign=\"top\" align=\"center\" class=\"style14\" style=\"width: 270px; height: 65pt; border-right-width: 1pt; border-right-style: dashed; border-right-color: windowtext;\"><font style=\"font-size: 14px;\"><b>C<font face=\"\'times new roman\'\">ỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM<br>              Độc lập - Tự do - Hạnh phúc</font></b></font><font face=\"\'times new roman\'\"><br>              ----------oOo----------</font></td>              <td valign=\"top\" align=\"center\" style=\"width: 180px;\"><font class=\"style12\" style=\"font-size: 14px;\" face=\"\'times new roman\'\"><b class=\"style14\"><font style=\"text-align: -webkit-center;\" face=\"\'times new roman\'\"><b class=\"style14\"><font class=\"style12\"><b class=\"style14\"><font style=\"