INSERT INTO `templatekey` VALUES ('57', null, '00000000-0000-0000-0000-000000000000', 'Giấy tờ bổ sung', '{doc_giay_to_bo_sung}', 'SELECT PaperName, Amount FROM `doc_paper` where SupplementaryId = @SuppId AND Type = 2;', '<ul>\r\n@foreach(var paper in Model){\r\n    <li>\r\n        @paper.PaperName\r\n        <span style=\"float: right\">@paper.Amount</span>\r\n    </li>\r\n}\r\n</ul>', '2', '', null, '');
