INSERT INTO `template` VALUES ('26', 'Mẫu email thông báo cuộc họp', '<h3 class=\"clssHeader\" style=\"color:blue;\">\r\n	<strong><span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Bạn được mời tham gia cuộc họp</span></span></strong></h3>\r\n<h3 class=\"clssHeader\" style=\"color:blue;\">\r\n	<strong><span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Cuộc họp dự kiến v&agrave;o l&uacute;c 09:00 thứ 4 - ng&agrave;y 16/04/2014</span></span></strong></h3>\r\n<h3 class=\"classEveDetail\" style=\"color:blue;\">\r\n	<strong><span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Chi tiết như sau:</span></span></strong></h3>\r\n<div class=\"clssEvent\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Ti&ecirc;u đề:&nbsp;</span></span>{meeting_title}</div>\r\n<div class=\"clssEvent\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Thời gian bắt đầu l&uacute;c:<span class=\"clssTime\">&nbsp;</span></span></span>{meeting_date}</div>\r\n<div class=\"clssEvent\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Thời gian kết th&uacute;c l&uacute;c:<span class=\"clssTime\">&nbsp;</span></span></span>{meeting_to_date}</div>\r\n<div class=\"clssEvent\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Người tạo lịch:&nbsp;</span></span>{meeting_creator}</div>\r\n<div class=\"clssEvent\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Cập nhật cuối c&ugrave;ng:&nbsp;</span></span>{meeting_last_update}</div>\r\n<div class=\"clssEvent\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Nội dung:&nbsp;</span></span>{meeting_body}\r\n	<div class=\"clssDescription\">\r\n		&nbsp;</div>\r\n</div>\r\n<div class=\"clss