INSERT INTO `report` VALUES ('226', null, 'Hồ sơ trễ hẹn', '224', '[17]', 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateReturned, \'%d/%m/%Y\') as DateReturned\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 4\r\nORDER BY DateCreated', null, 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateSuccess, \'%d/%m/%Y\') as DateSuccess,\r\n	DATE_FORMAT(IF(`d`.DateReturned is NULL, d.DateFinished, `d`.DateReturned ) , \'%d/%m/%Y\') as DateReturned,\r\n	GetStatus(d.`Status`) as Note\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 4\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'SELECT \r\n	#groupValue as GroupValue,\r\n	#groupName as GroupName,\r\n	COUNT(#groupValue) as Total\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nAND (((d.DateFinished is NOT NULL AND DateCompare(d.DateFinished, d.DateAppointed, null) = 1)\r\n					OR (d.DateSuccess is NOT NULL AND DateCompare(d.DateSuccess, d.DateAppointed, null) = 1) \r\n					Or (d.DateReturned is NOT NULL AND DateCompare(d.DateReturned, d.DateAppointed, null) = 1))\r\n				OR (d.`Status` = 2 \r\n							AND (((DateCompare(NOW(), d.DateAppointed, null) = 1) AND d.DateSuccess is NULL)\r\n								OR d.DateSuccess is NOT NULL and (DateCompare(d.DateSuccess, d.DateAppointed, null) = 1)))\r\n				OR (d.`Status` = 16 \r\n							AND DateCompare((SELECT sp.DateSend FROM supplementary sp where sp.DocumentId = d.DocumentId Limit 1), d.DateAppointed, null) = 1)\r\n			)\r\n     group by #groupValue', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 4', null, '{\"Fields\":[{\"Name\":\"Số ký hiệu\",\"Key\":\"DocCode\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Tên công dân\",\"Key\":\"CitizenName\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Địa chỉ\",\"Key\":\"CitizenInfo\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Ngày tiếp nhận\",\"Key\":\"DateCreated\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Ngày trả kết quả\",\"Key\":\"Date