INSERT INTO `report` VALUES ('227', null, 'Hồ sơ chưa đến hạn', '224', '[17]', 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateReturned, \'%d/%m/%Y\') as DateReturned\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n  And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 2\r\nORDER BY DateCreated', null, 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateSuccess, \'%d/%m/%Y\') as DateSuccess,\r\n	DATE_FORMAT(IF(`d`.DateReturned is NULL, d.DateFinished, `d`.DateReturned ) , \'%d/%m/%Y\') as DateReturned,\r\n	GetStatus(d.`Status`) as Note\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 2\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'SELECT \r\n	#groupValue as GroupValue,\r\n	#groupName as GroupName,\r\n	COUNT(#groupValue) as Total\r\nFrom document AS d\r\nJOIN