INSERT INTO `report` VALUES ('236', null, 'Báo cáo thông tin khách hàng', '222', null, 'Select d.DocCode, \r\n			d.CitizenName, \r\n			dt.DocTypeName, \r\n			DATE_FORMAT(d.DateCreated,\'%d/%m/%y\') as DateCreated, \r\n			DATE_FORMAT(d.DateAppointed,\'%d/%m/%y\') as DateAppointed, \r\n			d.Phone, d.Address\r\nFROM document d \r\nJOIN doctype dt on d.DocTypeId = dt.DocTypeId\r\nWHERE d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId = 4\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nORDER BY dt.DocTypeName;', null, 'Select d.DocCode, \r\n			d.CitizenName, \r\n			dt.DocTypeName, \r\n			DATE_FORMAT(d.DateCreated,\'%d/%m/%y\') as DateCreated, \r\n			DATE_FORMAT(d.DateAppointed,\'%d/%m/%y\') as DateAppointed, \r\n			d.Phone, d.Address\r\nFROM document d \r\nJOIN doctype dt on d.DocTypeId = dt.DocTypeId\r\nWHERE d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId = 4\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nORDER BY dt.DocTypeName;\r\n', 'Select \r\n#groupValue as GroupValue,\r\n	#groupName as GroupName,\r\n	COUNT(#groupValue) as Total\r\nFROM document d \r\nJOIN doctype dt on d.DocTypeId = dt.DocTypeId\r\nWHERE d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId = 4\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nORDER BY dt.DocTypeName;\r\n', 'Select Count(1)\r\nFROM document d \r\nJOIN doctype dt on d.DocTypeId = dt.DocTypeId\r\nWHERE d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId = 4\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nORDER BY dt.DocTypeName;\r\n', null, '{\"Fields\":[{\"Name\":\"Số ký hiệu\",\"Key\":\"DocCode\",\"Type\":1,\"DataType\":\"string\"},{