INSERT INTO `reportgroup` VALUES ('6', 'Loại hồ sơ', 'SELECT doctype.DocTypeName as GroupName, doctype.DocTypeId as GroupValue \r\nFROM doctype', 'dt.DocTypeName', 'd.DocTypeId', '');
INSERT INTO `reportgroup` VALUES ('7', 'Nhân viên cấp dưới', 'CALL LayCanBoCapDuoi', 'u.FullName', 'df.UserId', '');
INSERT INTO `reportgroup` VALUES ('8', 'Phòng ban (tổng hợp)', 'Select dept.DepartmentName as GroupName, dept.DepartmentId as GroupValue\r\n from department dept\r\nINNER JOIN user_department_jobtitles_position udjp\r\non dept.DepartmentId = udjp.DepartmentId\r\nWHERE udjp.UserId =@userId', 'dept.DepartmentName', 'dept.DepartmentId', '');
INSERT INTO `reportgroup` VALUES ('9', 'Hình thức văn bản', 'SELECT category.CategoryName as GroupName, category.CategoryId as GroupValue\r\nFrom category', 'category.CategoryName ', 'category.CategoryId', '');
INSERT INTO `reportgroup` VALUES ('10', 'Trạng thái', '(Select 1 as \'Status\', \'Dự thảo\' as \'StatusName\')\r\nunion (Select 2 as \'Status\', \'Đang xử lý\' as \'StatusName\')\r\nunion (Select 4 as \'Status\', \'Kết thúc\' as \'StatusName\')\r\nunion (Select 8 as \'Status\', \'Loại bỏ\' as \'StatusName\')\r\nunion (Select 16 as \'Status\', \'Dừng xử lý\' as \'StatusName\')', 'fnGetDocumentStatus(`d`.`Status`)', 'd.Status', '');
INSERT INTO `reportgroup` VALUES ('11', 'Sổ văn bản đến', 'SELECT st.StoreId as GroupValue, st.StoreName as GroupName\r\nfrom store st\r\nWHERE \r\nst.CategoryBusinessId = 1\r\nand (st.UserId = @userId\r\nOR st.UserViewIds like CONCAT(\'%,\',@userId,\',%\')\r\nOR st.UserViewIds like CONCAT(\'[\',@userId,\'%\')\r\nOR st.UserViewIds like CONCAT(\'%\',@userId,\']\'))', 'st.StoreName', 'sd.StoreId', '');
INSERT INTO `reportgroup` VALUES ('12', 'Người xử lý', 'select \r\n	`user`.`FullName` as `GroupName`, \r\n	`user`.`UserId` as `GroupValue`\r\nFROM `user` \r\nwhere \r\n`user`.`UserId` IN(\r\n    select u.`UserId` FROM `user` u INNER JOIN user_department_jobtitles_position udjp\r\n    on u.UserId = udjp.UserId\r\nwhere udjp.DepartmentId in (\r\nSELECT udjp2.DepartmentId from user_department_jobtitles_position udjp2 \r\nwhere udjp2.UserId = @userId\r\n));', 'u3.FullName', 'df.UserId', '');
INSERT INTO `reportgroup` VALUES ('14', 'Trạng thái văn bản', 'Call LayTrangThaiVanBan', 'fnGetDocumentStatus(`dc`.`Status`)', 'dc.Status', '');
INSERT INTO `reportgroup` VALUES ('15', 'Theo năm', 'Call LayCacNamKhac', 'Ngày tạo', 'YEAR(d.DateCreated)', '');
INSERT INTO `reportgroup` VALUES ('17', 'Theo lĩnh vực', 'SELECT df.DocFieldName as GroupName, df.DocFieldId as GroupValue \r\nFROM DocField df where df.IsActivated = 1', 'df.DocFieldName', 'df.DocFieldId', '');
INSERT INTO `reportgroup` VALUES ('18', 'Theo loại văn bản(thống kê)', 'SELECT `dt`.`DocTypeName` as `GroupName`, `dt`.`DocTypeId` as `GroupValue` \r\nFROM `doctype`  as `dt`  WHERE  `dt`.`IsActivated` = 1', 'DocTypeName ', 'DocTypeId', '');
INSERT INTO `reportgroup` VALUES ('19', 'Sổ văn bản đi', 'SELECT st.StoreId as GroupValue, st.StoreName as GroupName\r\nfrom store st\r\nWHERE \r\nst.CategoryBusinessId = 2\r\nand (st.UserId = @userId\r\nOR st.UserViewIds like CONCAT(\'%,\',@userId,\',%\')\r\nOR st.UserViewIds like CONCAT(\'[\',@userId,\'%\')\r\nOR st.UserViewIds like CONCAT(\'%\',@userId,\']\'))', 'st.StoreName', 'sd.StoreId', '');
INSERT INTO `reportgroup` VALUES ('20', 'Sổ hồ sơ một cửa', 'SELECT st.StoreId as GroupValue, st.StoreName as GroupName\r\nfrom store st\r\nWHERE \r\nst.CategoryBusinessId = 4\r\nand (st.UserId = @userId\r\nOR st.UserViewIds like CONCAT(\'%,\',@userId,\',%\')\r\nOR st.UserViewIds like CONCAT(\'[\',@userId,\',%\')\r\nOR st.UserViewIds like CONCAT(\'%,\',@userId,\']\'))', 'sd.StoreName', 'sd.StoreId', '');
INSERT INTO `reportgroup` VALUES ('21', 'Đơn vị soạn thảo', 'Select DepartmentPath as GroupValue, DepartmentPath as GroupName\r\nfrom department', 'd.InOutPlace', 'd.InOutPlace', '');
