INSERT INTO `report` VALUES ('237', null, 'Báo cáo Giấy phép đăng kí doanh nghiệp', '222', '[6,17]', 'Select ct.CitizenName as CitizenName\r\n, dt.DocTypeName as DocTypeName\r\n, bl.LicenseCode as LicenseCode\r\n, bl.LicenseNumber as LicenseNumber\r\n, DATE_FORMAT(bl.RegisDate, \'%d/%m/%Y\')  as RegisDate\r\n, DATE_FORMAT( bl.ExpireDate, \'%d/%m/%Y\') as ExpireDate\r\nfrom \r\nbusinesslicense as bl,\r\n documentcopy as dc, \r\ncitizen as ct,\r\n document as d,\r\n doctype as dt \r\nWHERE \r\nbl.DocumentCopyId = dc.DocumentCopyId and\r\n bl.BusinessId =ct.Id and \r\ndc.DocumentId = d.DocumentId and \r\nd.DocTypeId = dt.DocTypeId', null, 'Select \r\nct.CitizenName as CitizenName\r\n, dt.DocTypeName as DocTypeName\r\n, bl.LicenseCode as LicenseCode\r\n, bl.LicenseNumber as LicenseNumber\r\n, DATE_FORMAT(bl.RegisDate, \'%d/%m/%Y\')  as RegisDate\r\n, DATE_FORMAT( bl.ExpireDate, \'%d/%m/%Y\') as ExpireDate\r\nfrom \r\nbusinesslicense as bl,\r\n documentcopy as dc, \r\ncitizen as ct,\r\n document as d,\r\n doctype as dt \r\nWHERE \r\nbl.DocumentCopyId = dc.DocumentCopyId and\r\n bl.BusinessId =ct.Id and \r\ndc.DocumentId = d.DocumentId and \r\nd.DocTypeId = dt.DocTypeId \r\nAND DATEDIFF(d.DateCreated, @from) >= 0 \r\nAND DATEDIFF(d.DateCreated, @to) <= 0\r\nAND \r\n(@groupValue = \'\' or #groupValue = @groupValue)', 'SELECT \r\n	#groupValue as GroupValue,\r\n	#groupName as GroupName,\r\n	COUNT(#groupValue) as Total\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\n     group by #groupValue', 'select count(1) \r\nfrom \r\nbusinesslicense as bl,\r\n documentcopy as dc, \r\ncitizen as ct,\r\n document as d,\r\n doctype as dt \r\nWHERE \r\nbl.DocumentCopyId = dc.DocumentCopyId and\r\n bl.BusinessId =ct.Id and \r\ndc.DocumentId = d.DocumentId \r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nand \r\nd.DocTypeId = dt.DocTypeId AND \r\n(@groupValue = \'\' or #groupValue = @groupValue)', null, '{\"Fields\":[{\"Name\":\"Tên Doanh Nghiệp\",\"Key\":\"CitizenName\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Tên Thủ Tục\",\"Key\":\"DocTypeName\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Mã Cấp Phép\",\"Key\":\"LicenseCode\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Số Cấp Phép\",\"Key\":\"LicenseNumber\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Ngày đăng ký\",\"Key\":\"RegisDate\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Ngày Hết Hạn\",\"Key\":\"ExpireDate\",\"Type\":1,\"DataType\":\"string\"},{\"Name\":\"Ngày tháng hiện tại\",\"Key\":\"CurrentDate\",\"Type\":2,\"DataType\":\"datetime\"},{\"Name\":\"Từ thời gian\",\"Key\":\"FromDate\",\"Type\":2,\"DataType\":\"datetime\"},{\"Name\":\"Đến thời gian\",\"Key\":\"ToDate\",\"Type\":2,\"DataType\":\"datetime\"},{\"Name\":\"Số thứ tự\",\"Key\":\"Stt\",\"Type\":2,\"DataType\":\"number\"},{\"Name\":\"Tên nhóm\",\"Key\":\"GroupName\",\"Type\":3,\"DataType\":\"string\"},{\"Name\":\"Số lượng\",\"Key\":\"GroupCount\",\"Type\":3,\"DataType\":\"number\"},{\"Name\":\"Số thứ tự\",\"Key\":\"gStt\",\"Type\":3,\"DataType\":\"number\"}],\"UsedFields\":[{\"Name\":\"Tên nhóm\",\"Key\":\"[GroupName]\",\"Type\":3,\"Datatype\":\"string\",\"Style\":{\"Top\":0,\"Left\":0,\"Width\":708,\"Height\":15,\"FullStyle\":\"width: 949px; top: 0px; left: 0px; height: 20px;\"}},{\"Name\":\"Mã Cấp Phép\",\"Key\":\"[LicenseCode]\",\"Type\":1,\"Datatype\":\"string\",\"Style\":{\"Top\":0,\"Left\":0,\"Width\":108.75,\"Height\":15,\"FullStyle\":\"width: 151px;\"}},{\"Name\":\"Tên Doanh Nghiệp\",\"Key\":\"[CitizenName]\",\"Type\":1,\"Datatype\":\"string\",\"Style\":{\"Top\":0,\"Left\":113.25,\"Width\":114,\"Height\":15,\"FullStyle\":\"width: 157px;\"}},{\"Name\":\"Tê