INSERT INTO `report` VALUES ('223', null, 'Sổ theo dõi hồ sơ', '222', null, 'SELECT d.DocCode, dt.DocTypeName, 1 as Amount, d.CitizenName, \r\n	CONCAT(d.Phone,\' - \',d.Address) as Address, \"\" as `Department`, \r\n	DATE_FORMAT(d.DateCreated,\'%d/%m/%y\') as DateCreated, \r\n	DATE_FORMAT(d.DateAppointed,\'%d/%m/%y\') as DateAppointed, \r\n	DATE_FORMAT(d.DateCreated,\'%d/%m/%y\') as DateResult, \r\n	DATE_FORMAT(d.DateSuccess,\'%d/%m/%y\') as DateFinished,\r\n	DATE_FORMAT(d.DateReturned,\'%d/%m/%y\') as DateReturned\r\nFROM document d\r\nJOIN documentcopy dc ON dc.DocumentId = d.DocumentId\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJoin docfield df on df.DocFieldId = dt.DocFieldId\r\nWHERE d.CategoryBusinessId = 4\r\n        AND d.`Status` NOT IN (1, 8)\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n        And dc.DocumentCopyType  in (1, 64)\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY dt.DocTypeName, d.DocCode\r\nLIMIT @skip, @take;', 'SELECT Count(1)\r\nFROM document d\r\nJOIN documentcopy dc ON dc.DocumentId = d.DocumentId\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJoin docfield df on df.DocFieldId = dt.DocFieldId\r\nWHERE d.CategoryBusinessId = 4\r\n        AND d.`Status` NOT IN (1, 8)\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n        And dc.DocumentCopyType in (1, 64)\r\n       AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY dt.DocTypeName;', 'BaoCao_SoTheoDoiHoSo.rpt', '931588242bd64853a9830895039ef0c3.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '1');
INSERT INTO `report` VALUES ('224', null, 'Báo cáo tiếp nhận hồ sơ', '222', null, 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateReturned, \'%d/%m/%Y\') as DateReturned\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY d.DocCode\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)', 'BaoCao_TiepNhanHoSo.rpt', 'd7657df9127840babff4c26e1eae64b3.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '1');
INSERT INTO `report` VALUES ('225', null, 'Hồ sơ đúng hẹn', '224', null, 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateSuccess, \'%d/%m/%Y\') as DateSuccess,\r\n	DATE_FORMAT(IF(`d`.DateReturned is NULL, d.DateFinished, `d`.DateReturned ) , \'%d/%m/%Y\') as DateReturned,\r\n	GetStatus(d.`Status`) as Note\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 1\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 1', 'BaoCao_TiepNhanHoSo.rpt', '2170c09808964129b13a28e361c321ef.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'select 0;', 'select 0;', '', '1');
