INSERT INTO `report` VALUES ('227', null, 'Hồ sơ chưa đến hạn', '224', null, 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateSuccess, \'%d/%m/%Y\') as DateSuccess,\r\n	DATE_FORMAT(IF(`d`.DateReturned is NULL, d.DateFinished, `d`.DateReturned ) , \'%d/%m/%Y\') as DateReturned,\r\n	GetStatus(d.`Status`) as Note\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 2\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n  And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 2', 'BaoCao_TiepNhanHoSo - ChuaDenHan.rpt', '1fa086ae1e3f41d7be687fb674ba96ad.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'select 0;', 'select 0;', '', '1');
INSERT INTO `report` VALUES ('228', null, 'Hồ sơ quá hạn', '224', null, 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateSuccess, \'%d/%m/%Y\') as DateSuccess,\r\n	DATE_FORMAT(IF(`d`.DateReturned is NULL, d.DateFinished, `d`.DateReturned ) , \'%d/%m/%Y\') as DateReturned,\r\n	GetStatus(d.`Status`) as Note\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 3\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` not in (1, 8) \r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 3', 'BaoCao_TiepNhanHoSo - ChuaDenHan.rpt', '6c052f72a10842b190527139b3ab57ba.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'select 0;', 'select 0;', '', '1');
INSERT INTO `report` VALUES ('229', null, 'Báo cáo Hồ sơ tới hạn', '222', null, 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateSuccess, \'%d/%m/%Y\') as DateSuccess,\r\n	DATE_FORMAT(IF(`d`.DateReturned is NULL, d.DateFinished, `d`.DateReturned ) , \'%d/%m/%Y\') as DateReturned,\r\n	GetStatus(d.`Status`) as Note\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` in (2, 16) \r\n	AND d.IsSuccess is NULL \r\n	AND d.IsReturned is NULL\r\n        And d.IsConverted = FALSE\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 2\r\n        And DateCompare(NOW(),d.DateAppointed, null) = 0\r\n	AND DATEDIFF(d.DateAppointed, NOW()) < 4\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` in (2, 16) \r\n	AND d.IsSuccess is NULL \r\n	AND d.IsReturned is NULL\r\n        And d.IsConverted = FALSE\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND d.DocTypeId is NOT NULL\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n	AND OverdueStatus(d.`Status`, d.DateAppointed, d.DateSuccess, d.DateReturned, d.DateFinished, d.DateRequireSupplementary, @to) = 2\r\n        And DateCompare(NOW(),d.DateAppointed, null) = 0\r\n	AND DATEDIFF(d.DateAppointed, NOW()) <=3', 'BaoCao_TiepNhanHoSo - ToiHan.rpt', 'ba4ff9dc63314c10b6e2c3adc266c61f.rpt', null, null, null, '', '',