INSERT INTO `report` VALUES ('231', null, 'Văn bản đến', '230', null, 'Select 	\r\n	d.InOutCode, \r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.Organization, \r\n	DATE_FORMAT(d.DateArrived, \'%d/%m/%Y\') as DateArrived,\r\n	category.CategoryName,\r\n	category.CategoryId,\r\n	u.FullName as UserProcess,\r\n        u.UserId as UserCurrentId,\r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%Y\') as DateAppointed,	\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	ug.UrgentName,\r\n	dc.DocumentCopyId\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = dc.UserCurrentId\r\njoin urgent ug on ug.UrgentId = d.UrgentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 1\r\n	AND d.Original = 2\r\n	AND d.CategoryId is NOT NULL\r\n	AND TIMEDIFF(d.DateArrived, @from) >= 0 \r\n	AND TIMEDIFF(d.DateArrived, @to) <= 0\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateArrived;', null, null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '261');
INSERT INTO `report` VALUES ('233', null, 'Hồ sơ một cửa liên thông', '230', null, 'select d.DocCode, d.Compendium, dp.AddressName, d.CitizenName, \r\n	DATE_FORMAT(dp.DatePublished, \'%d/%m/%Y\') as DatePublished, 	\r\n	DATE_FORMAT(dp.DateResponsed, \'%d/%m/%Y\') as DateResponsed\r\nFROM doc_publish dp\r\nJOIN document d on d.DocumentId = dp.DocumentId\r\nWHERE dp.HasLienThong = 1\r\n        And d.CategoryBusinessId = 4\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY dp.DatePublished', 'select Count(1)\r\nFROM doc_publish dp\r\nJOIN document d on d.DocumentId = dp.DocumentId\r\nWHERE dp.HasLienThong = 1\r\n        And d.CategoryBusinessId = 4\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\n', 'BaoCao_HoSoLienThong.rpt', '57491a77b577441ca87cd048a0c6d83a.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'select 0;', 'select 0;', '', '1');
