INSERT INTO `report` VALUES ('235', null, 'Báo cáo hồ sơ đăng ký qua mạng', '234', null, 'Select d.DocCode, \r\n			d.CitizenName, \r\n			dt.DocTypeName, \r\n			DATE_FORMAT(d.DateCreated,\'%d/%m/%y\') as DateCreated, \r\n			DATE_FORMAT(d.DateAppointed,\'%d/%m/%y\') as DateAppointed\r\nFROM document d \r\nJOIN doctype dt on d.DocTypeId = dt.DocTypeId\r\nWHERE d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId = 4\r\n	AND d.Original = 1\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nORDER BY dt.DocTypeName;', 'Select Count(1)\r\nFROM document d \r\nJOIN doctype dt on d.DocTypeId = dt.DocTypeId\r\nWHERE d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId = 4\r\n	AND d.Original = 1\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nORDER BY dt.DocTypeName;', 'BaoCao_TiepNhanHoSo.rpt', '84a71f6fa2064553afd0b0993651f32a.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '1');
INSERT INTO `report` VALUES ('236', null, 'Báo cáo thông tin khách hàng', '222', null, 'Select d.DocCode, \r\n			d.CitizenName, \r\n			dt.DocTypeName, \r\n			DATE_FORMAT(d.DateCreated,\'%d/%m/%y\') as DateCreated, \r\n			DATE_FORMAT(d.DateAppointed,\'%d/%m/%y\') as DateAppointed, \r\n			d.Phone, d.Address\r\nFROM document d \r\nJOIN doctype dt on d.DocTypeId = dt.DocTypeId\r\nWHERE d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId = 4\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nORDER BY dt.DocTypeName;\r\n', 'Select Count(1)\r\nFROM document d \r\nJOIN doctype dt on d.DocTypeId = dt.DocTypeId\r\nWHERE d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId = 4\r\n	AND d.DocCode is NOT NULL\r\n	AND d.DocCode != \'\'\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\nORDER BY dt.DocTypeName;\r\n', 'BaoCao_Khachhangdangki.rpt', 'e3ac1ea3b58b4c929e9cc91394ab9945.rpt', null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '1');
INSERT INTO `report` VALUES ('238', null, 'Theo chi nhánh', '224', '[17]', 'Select \r\n	`d`.DocCode,\r\n	`d`.CitizenName,\r\n	`d`.CitizenInfo,\r\n	DATE_FORMAT(`d`.DateCreated, \'%d/%m/%Y\') as DateCreated,\r\n	DATE_FORMAT(`d`.DateAppointed, \'%d/%m/%Y\') as DateAppointed,\r\n	DATE_FORMAT(`d`.DateReturned, \'%d/%m/%Y\') as DateReturned\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)\r\nORDER BY DateCreated\r\nLIMIT @skip, @take;', 'Select \r\n	Count(1)\r\nFrom document AS d\r\nJOIN doctype dt on dt.DocTypeId = d.DocTypeId\r\nJOIN docfield df on dt.DocFieldId = df.DocFieldId\r\nWHERE d.CategoryBusinessId = 4 -- La hsmc\r\n	AND d.`Status` NOT IN (1, 8) -- Khong phai la loai bo va du theo\r\n	AND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n       AND (@treeGroupValue = \'\'   or @treeGroupValue is null  or #treeGroup = @treeGroupValue)', 'BaoCao_TiepNhanHoSo.rpt', null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '1');
INSERT INTO `report` VALUES ('240', null, 'Văn bản đi', '230', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode,\r\n	d.ProcessInfo,\r\n	DATE_FORMAT(dp.DatePublished, \'%d/%m/%Y\') as DatePublished,\r\n	category.CategoryName,\r\n	us.FullName as UserSuccess,\r\n	u.FullName as UserCreated,\r\n	d.InOutPlace,\r\n	category.CategoryId,\r\n	ug.UrgentId, ug.UrgentName,\r\n	d.UserCreatedId,\r\n	d.UserSuccessId,\r\n	dc.DocumentCopyId\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN doc_publish dp on dp.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = d.UserCreatedId\r\nJOIN `user` us on us.UserId = d.UserSuccessId\r\nLEFT JOIN urgent ug on ug.UrgentId = d.UrgentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` = 4\r\n	AND d.CategoryBusinessId = 2\r\n	AND dp.HasLienThong = 1\r\n	AND d.CategoryId is NOT NULL\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nGROUP BY dp.DocumentId\r\nORDER BY d.DateCreated;', null, null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '262');
INSERT INTO `report` VALUES ('241', null, 'Văn bản tới hạn', '210', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.InOutCode,\r\n	category.CategoryName,\r\n	category.CategoryId,\r\n	d.UserCreatedId,\r\n	dc.UserCurrentId,\r\n	u.FullName as UserCreated, \r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%y\') as ExpireDate,\r\n	DATE_FORMAT(d.DateFinished, \'%d/%m/%y\') as FinishDate,\r\n	d.DateFinished,\r\n	d.DateAppointed,\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	ug.UrgentName,\r\n	dc.DocumentCopyId,\r\n	d.CategoryBusinessId,\r\n	GetCategoryBusiness(d.CategoryBusinessId) as CategoryBusiness\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = d.UserCreatedId\r\njoin urgent ug on ug.UrgentId = d.UrgentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType in (1, 2)\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId != 4	\r\n	AND d.CategoryId is NOT NULL\r\n	AND dc.UserCurrentId = @userId\r\n	AND d.DateAppointed is not NULL \r\n	AND d.DateFinished is null\r\n	AND d.DateAppointed > NOW()\r\n	AND DATEDIFF(d.DateAppointed, NOW()) < 3\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'select count(1)\r\nFROM `document` AS `d`\r\nJOIN `documentcopy` AS `dc` ON `dc`.`DocumentId` = `d`.`DocumentId`\r\nJOIN `user` AS `u` ON `u`.`UserId` = `dc`.`UserCurrentId`\r\nJOIN `user` AS `u1` ON `u1`.`UserId` = `d`.`UserCreatedId`\r\nJOIN `doctype` AS `dt` ON `d`.`DocTypeId` = `dt`.`DocTypeId`\r\nJOIN `docfinish` AS `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId` \r\nJOIN `category` ON `d`.`CategoryId` = `category`.`CategoryId`\r\nLEFT OUTER JOIN `user` AS `u2` ON `u2`.`UserId` = `d`.`UserSuccessId`\r\nLEFT OUTER JOIN `docfield` AS `df2` ON `dt`.DocFieldId = `df2`.`DocFieldId`\r\nLEFT OUTER JOIN `doc_publish` AS `dp` ON `dp`.`DocumentCopyId` = `dc`.`DocumentCopyId`\r\n     WHERE `df`.`UserId` = @UserId and\r\n`d`.`Status` IN(4) and\r\n	  d.DateAppointed < d.DateSuccess\r\nAND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n    AND (@treeGroupValue is null or @treeGroupValue = \'\' or #treeGroup = @treeGroupValue)\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)', null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '264');
