INSERT INTO `report` VALUES ('243', null, 'Văn bản tới hạn', '242', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.InOutCode,\r\n	category.CategoryName,\r\n	category.CategoryId,\r\n	d.UserCreatedId,\r\n	dc.UserCurrentId,\r\n	u.FullName as UserCurrent, \r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%y\') as ExpireDate,\r\n	DATE_FORMAT(d.DateFinished, \'%d/%m/%y\') as FinishDate,\r\n	d.DateFinished,\r\n	d.DateAppointed,\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	ug.UrgentName,\r\n	dc.DocumentCopyId,\r\n	dp.DepartmentName,\r\n	dp.DepartmentId,\r\n	d.CategoryBusinessId,\r\n	GetCategoryBusiness(d.CategoryBusinessId) as CategoryBusiness\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = dc.UserCurrentId\r\njoin urgent ug on ug.UrgentId = d.UrgentId\r\nJOIN user_department_jobtitles_position ud on ud.UserId = u.UserId\r\nJOIN department dp on dp.DepartmentId = ud.DepartmentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType in (1, 2)\r\n	AND d.`Status` NOT IN (1, 8, 4)\r\n	AND d.CategoryBusinessId != 4	\r\n	AND d.CategoryId is NOT NULL\r\n	AND d.DateAppointed is not NULL \r\n	AND d.DateFinished is null\r\n	AND d.DateAppointed > NOW()\r\n	AND DATEDIFF(d.DateAppointed, NOW()) < 3\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'select count(1)\r\nFROM `document` AS `d`\r\nJOIN `documentcopy` AS `dc` ON `dc`.`DocumentId` = `d`.`DocumentId`\r\nJOIN `user` AS `u` ON `u`.`UserId` = `dc`.`UserCurrentId`\r\nJOIN `user` AS `u1` ON `u1`.`UserId` = `d`.`UserCreatedId`\r\nJOIN `doctype` AS `dt` ON `d`.`DocTypeId` = `dt`.`DocTypeId`\r\nJOIN `docfinish` AS `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId` \r\nJOIN `category` ON `d`.`CategoryId` = `category`.`CategoryId`\r\nLEFT OUTER JOIN `user` AS `u2` ON `u2`.`UserId` = `d`.`UserSuccessId`\r\nLEFT OUTER JOIN `docfield` AS `df2` ON `dt`.DocFieldId = `df2`.`DocFieldId`\r\nLEFT OUTER JOIN `doc_publish` AS `dp` ON `dp`.`DocumentCopyId` = `dc`.`DocumentCopyId`\r\n     WHERE `df`.`UserId` = @UserId and\r\n`d`.`Status` IN(4) and\r\n	  d.DateAppointed < d.DateSuccess\r\nAND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n    AND (@treeGroupValue is null or @treeGroupValue = \'\' or #treeGroup = @treeGroupValue)\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)', null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '264');
INSERT INTO `report` VALUES ('244', null, 'Văn bản đã xử lý - Quá hạn', '242', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.InOutCode,\r\n	category.CategoryName,\r\n	category.CategoryId,\r\n	d.UserCreatedId,\r\n	dc.UserCurrentId,\r\n	u.FullName as UserCurrent, \r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%y\') as ExpireDate,\r\n	DATE_FORMAT(dc.DateFinished, \'%d/%m/%y\') as FinishDate,\r\n	dc.DateFinished,\r\n	d.DateAppointed,\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	ug.UrgentName,\r\n	dc.DocumentCopyId,\r\n	dp.DepartmentName,\r\n	dp.DepartmentId,\r\n	d.CategoryBusinessId,\r\n	GetCategoryBusiness(d.CategoryBusinessId) as CategoryBusiness\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = dc.UserCurrentId\r\njoin urgent ug on ug.UrgentId = d.UrgentId\r\nJOIN user_department_jobtitles_position ud on ud.UserId = dc.UserCurrentId\r\nJOIN department dp on dp.DepartmentId = ud.DepartmentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType in (1, 2)\r\n	AND dc.`Status` = 4\r\n	AND d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId != 4	\r\n	AND d.CategoryId is NOT NULL\r\n	AND d.DateAppointed is not NULL \r\n	AND dc.DateFinished is not null\r\n	AND Date(dc.DateFinished) > Date(d.DateAppointed)\r\n	AND Date(d.DateCreated) >= Date(@from)\r\n	AND Date(d.DateCreated) <= Date(@to)\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'select count(1)\r\nFROM `document` AS `d`\r\nJOIN `documentcopy` AS `dc` ON `dc`.`DocumentId` = `d`.`DocumentId`\r\nJOIN `user` AS `u` ON `u`.`UserId` = `dc`.`UserCurrentId`\r\nJOIN `user` AS `u1` ON `u1`.`UserId` = `d`.`UserCreatedId`\r\nJOIN `doctype` AS `dt` ON `d`.`DocTypeId` = `dt`.`DocTypeId`\r\nJOIN `docfinish` AS `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId` \r\nJOIN `category` ON `d`.`CategoryId` = `category`.`CategoryId`\r\nLEFT OUTER JOIN `user` AS `u2` ON `u2`.`UserId` = `d`.`UserSuccessId`\r\nLEFT OUTER JOIN `docfield` AS `df2` ON `dt`.DocFieldId = `df2`.`DocFieldId`\r\nLEFT OUTER JOIN `doc_publish` AS `dp` ON `dp`.`DocumentCopyId` = `dc`.`DocumentCopyId`\r\n     WHERE `df`.`UserId` = @UserId and\r\n`d`.`Status` IN(4) and\r\n	  d.DateAppointed < d.DateSuccess\r\nAND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n    AND (@treeGroupValue is null or @treeGroupValue = \'\' or #treeGroup = @treeGroupValue)\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)', null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '264');
INSERT INTO `report` VALUES ('245', null, 'Văn bản đang xử lý - Đúng hạn', '242', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.InOutCode,\r\n	category.CategoryName,\r\n	category.CategoryId,\r\n	d.UserCreatedId,\r\n	dc.UserCurrentId,\r\n	u.FullName as UserCurrent, \r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%y\') as ExpireDate,\r\n	DATE_FORMAT(dc.DateFinished, \'%d/%m/%y\') as FinishDate,\r\n	dc.DateFinished,\r\n	d.DateAppointed,\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	ug.UrgentName,\r\n	dc.DocumentCopyId,\r\n	dp.DepartmentName,\r\n	dp.DepartmentId,\r\n	d.CategoryBusinessId,\r\n	GetCategoryBusiness(d.CategoryBusinessId) as CategoryBusiness\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = dc.UserCurrentId\r\njoin urgent ug on ug.UrgentId = d.UrgentId\r\nJOIN user_department_jobtitles_position ud on ud.UserId = u.UserId\r\nJOIN department dp on dp.DepartmentId = ud.DepartmentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType in (1, 2)\r\n	AND dc.`Status` != 4\r\n	AND d.`Status` not in (1, 8)\r\n	AND d.CategoryBusinessId != 4	\r\n	AND d.CategoryId is NOT NULL\r\n	AND dc.DateFinished is null\r\n	AND d.DateAppointed >= NOW()\r\n	AND Date(d.DateCreated) >= Date(@from)\r\n	AND Date(d.DateCreated) <= Date(@to)\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', 'select count(1)\r\nFROM `document` AS `d`\r\nJOIN `documentcopy` AS `dc` ON `dc`.`DocumentId` = `d`.`DocumentId`\r\nJOIN `user` AS `u` ON `u`.`UserId` = `dc`.`UserCurrentId`\r\nJOIN `user` AS `u1` ON `u1`.`UserId` = `d`.`UserCreatedId`\r\nJOIN `doctype` AS `dt` ON `d`.`DocTypeId` = `dt`.`DocTypeId`\r\nJOIN `docfinish` AS `df` ON `dc`.`DocumentCopyId` = `df`.`DocumentCopyId` \r\nJOIN `category` ON `d`.`CategoryId` = `category`.`CategoryId`\r\nLEFT OUTER JOIN `user` AS `u2` ON `u2`.`UserId` = `d`.`UserSuccessId`\r\nLEFT OUTER JOIN `docfield` AS `df2` ON `dt`.DocFieldId = `df2`.`DocFieldId`\r\nLEFT OUTER JOIN `doc_publish` AS `dp` ON `dp`.`DocumentCopyId` = `dc`.`DocumentCopyId`\r\n     WHERE `df`.`UserId` = @UserId and\r\n`d`.`Status` IN(4) and\r\n	  d.DateAppointed < d.DateSuccess\r\nAND DATEDIFF(d.DateCreated, @from) >= 0 \r\n	AND DATEDIFF(d.DateCreated, @to) <= 0\r\n    AND (@treeGroupValue is null or @treeGroupValue = \'\' or #treeGroup = @treeGroupValue)\r\n	AND (@groupValue = \'\' or #groupValue = @groupValue)', null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '264');
