INSERT INTO `report` VALUES ('250', null, 'Văn bản đi', '249', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode,\r\n	d.ProcessInfo,\r\n	category.CategoryName,\r\n	DATE_FORMAT(d.DateFinished, \'%d/%m/%Y\') as DatePublished,\r\n	us.FullName as UserSuccess,\r\n	u.FullName as UserCreated,\r\n	d.InOutPlace,\r\n	category.CategoryId,\r\n	ug.UrgentId, ug.UrgentName,\r\n	d.UserCreatedId,\r\n	d.UserSuccessId,\r\n	GROUP_CONCAT(att.AttachmentName) as AttachmentName,\r\n	dc.DocumentCopyId\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN attachment att on att.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = d.UserCreatedId\r\nJOIN `user` us on us.UserId = d.UserSuccessId\r\nLEFT JOIN urgent ug on ug.UrgentId = d.UrgentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType = 1\r\n	And d.`Status` not in (1, 8) \r\n	AND d.CategoryBusinessId = 2\r\n	AND d.CategoryId is NOT NULL\r\n	AND att.AttachmentName LIKE \'%_Signed.%\'\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nGROUP BY d.DocumentId\r\nORDER BY d.DateCreated;', null, null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '262');
INSERT INTO `report` VALUES ('251', null, 'Văn bản đến', '249', null, 'Select 	\r\n	d.InOutCode, \r\n	d.Compendium,\r\n	d.DocCode, \r\n	d.Organization, \r\n	DATE_FORMAT(d.DateArrived, \'%d/%m/%Y\') as DateArrived,\r\n	category.CategoryName,\r\n	category.CategoryId,\r\n	u.FullName as UserProcess,\r\n        u.UserId as UserCurrentId,\r\n	DATE_FORMAT(d.DateAppointed, \'%d/%m/%Y\') as DateAppointed,	\r\n	d.`Status`,\r\n	GetStatus(d.`Status`) as StatusName,\r\n	d.UrgentId,\r\n	ug.UrgentName,\r\n	dc.DocumentCopyId\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN attachment att on att.DocumentId = d.DocumentId\r\nJOIN `user` u on u.UserId = dc.UserCurrentId\r\njoin urgent ug on ug.UrgentId = d.UrgentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` NOT IN (1, 8)\r\n	AND d.CategoryBusinessId = 1\r\n	AND att.AttachmentName LIKE \'%_Signed.%\'\r\n	AND d.CategoryId is NOT NULL\r\n	AND TIMEDIFF(d.DateArrived, @from) >= 0 \r\n	AND TIMEDIFF(d.DateArrived, @to) <= 0\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateArrived;', null, null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '261');
INSERT INTO `report` VALUES ('253', null, 'Văn bản theo dõi hồi báo', '230', null, 'Select 	\r\n	d.Compendium,\r\n	d.DocCode,\r\n	dp.AddressName,\r\n	DATE_FORMAT(dp.DatePublished, \'%d/%m/%Y\') as DatePublished,\r\n	category.CategoryName,\r\n	us.FullName as UserSuccess,\r\n	uc.FullName as UserCurrent,\r\n	DATE_FORMAT(dp.DateAppointed, \'%d/%m/%Y\') as DateAppointed,	\r\n	dp.DocCodeResponsed,\r\n	category.CategoryId,\r\n	ug.UrgentId, ug.UrgentName,\r\n	dc.UserCurrentId,\r\n	d.UserSuccessId,\r\n	dp.AddressId,\r\n	dc.DocumentCopyId\r\nFROM document d\r\nJOIN documentcopy dc on dc.DocumentId = d.DocumentId\r\nJOIN doc_publish dp on dp.DocumentId = d.DocumentId\r\nJOIN `user` uc on uc.UserId = dc.UserCurrentId\r\nJOIN `user` us on us.UserId = d.UserSuccessId\r\nLEFT JOIN urgent ug on ug.UrgentId = d.UrgentId\r\nLeft JOIN category on category.CategoryId = d.CategoryId\r\nWHERE dc.DocumentCopyType = 1\r\n	AND d.`Status` = 4\r\n	AND d.CategoryBusinessId = 2\r\n	AND dp.HasLienThong = 1\r\n	AND dp.HasRequireResponse = 1\r\n	AND d.CategoryId is NOT NULL\r\n	AND TIMEDIFF(d.DateCreated, @from) >= 0 \r\n	AND TIMEDIFF(d.DateCreated, @to) <= 0\r\n	AND (@treeGroupValue IS NULL OR  @treeGroupValue=\'\' or #treeGroup = @treeGroupValue)\r\nORDER BY d.DateCreated;', null, null, null, null, null, null, '', '', '0001-01-01 00:00:00', 'Select 0;', 'Select 0;', '', '265');
