INSERT INTO `reportgroup` VALUES ('19', 'Sổ văn bản đi', 'SELECT st.StoreId as GroupValue, st.StoreName as GroupName\r\nfrom store st\r\nWHERE \r\nst.CategoryBusinessId = 2\r\nand (st.UserId = @userId\r\nOR st.UserViewIds like CONCAT(\'%,\',@userId,\',%\')\r\nOR st.UserViewIds like CONCAT(\'[\',@userId,\',%\')\r\nOR st.UserViewIds like CONCAT(\'%,\',@userId,\']\'))\r\nORDER BY StoreName;', 'StoreName', 'StoreId', '');
